#define BWA_TK_RPAREN    0x0000400 /* Right parenthesis ')' */
#define BWA_TK_OSB       0x0000800 /* Open square bracket '[' */
#define BWA_TK_CSB       0x0001000 /* Closing square bracket ']' */
