/*
 * bool class_exists(string $class_name [, bool $autoload = true ] )
 *   Checks if the class has been defined.
 * Parameters
 *  class_name
 *   The class name. The name is matched in a case-sensitive manner 
 *   unlinke the standard PHP engine.
 *  autoload
 *   Whether or not to call __autoload by default.
 * Return
 *   TRUE if class_name is a defined class, FALSE otherwise. 
 */
static int vm_builtin_class_exists(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	int res = 0; /* Assume class does not exists */
	if( nArg > 0 ){
		const char *zName;
		int nLen;
		/* Extract given name */
		zName = bwa_value_to_string(apArg[0],&nLen);
		/* Perform a hashlookup */
		if( nLen > 0 && SyHashGet(&pCtx->pVm->hClass,(const void *)zName,(sxu32)nLen) != 0 ){
			/* class is available */
			res = 1;
		}
	}
	bwa_result_bool(pCtx,res);
	return BWA_OK;
}
/*
 * bool interface_exists(string $class_name [, bool $autoload = true ] )
 *   Checks if the interface has been defined.
 * Parameters
 *  class_name
 *   The class name. The name is matched in a case-sensitive manner 
 *   unlinke the standard PHP engine.
 *  autoload
 *   Whether or not to call __autoload by default.
 * Return
 *   TRUE if class_name is a defined class, FALSE otherwise. 
 */
static int vm_builtin_interface_exists(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	int res = 0; /* Assume class does not exists */
	if( nArg > 0 ){
		SyHashEntry *pEntry = 0;
		const char *zName;
		int nLen;
		/* Extract given name */
		zName = bwa_value_to_string(apArg[0],&nLen);
		/* Perform a hashlookup */
		if( nLen > 0 ){
			pEntry = SyHashGet(&pCtx->pVm->hClass,(const void *)zName,(sxu32)nLen);
		}
		if( pEntry ){
			bwa_class *pClass = (bwa_class *)pEntry->pUserData;
			while( pClass ){
				if( pClass->iFlags & BWA_CLASS_INTERFACE ){
					/* interface is available */
					res = 1;
					break;
				}
				/* Next with the same name */
				pClass = pClass->pNextName;
			}
		}
	}
	bwa_result_bool(pCtx,res);
	return BWA_OK;
}
/*
 * bool class_alias([string $original[,string $alias ]])
 *   Creates an alias for a class.
 * Parameters
 *  original
 *    The original class.
 *  alias
 *   The alias name for the class.
 * Return
 *   Returns TRUE on success or FALSE on failure. 
 */
static int vm_builtin_class_alias(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zOld,*zNew;
	int nOldLen,nNewLen;
	SyHashEntry *pEntry;
	bwa_class *pClass;
	char *zDup;
	sxi32 rc;
	if( nArg < 2 ){
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract old class name */
	zOld = bwa_value_to_string(apArg[0],&nOldLen);
	/* Extract alias name */
	zNew = bwa_value_to_string(apArg[1],&nNewLen);
	if( nNewLen < 1 ){
		/* Invalid alias name,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Perform a hash lookup */
	pEntry = SyHashGet(&pCtx->pVm->hClass,(const void *)zOld,(sxu32)nOldLen);
	if( pEntry ==  0 ){
		/* No such class,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the class */
	pClass = (bwa_class *)pEntry->pUserData;
	/* Duplicate alias name */
	zDup = SyMemBackendStrDup(&pCtx->pVm->sAllocator,zNew,(sxu32)nNewLen);
	if( zDup == 0 ){
		/* Out of memory,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Create the alias */
	rc = SyHashInsert(&pCtx->pVm->hClass,(const void *)zDup,(sxu32)nNewLen,pClass);
	if( rc != SXRET_OK ){
		SyMemBackendFree(&pCtx->pVm->sAllocator,zDup);
	}
	bwa_result_bool(pCtx,rc == SXRET_OK);
	return BWA_OK;
}
/*
 * array get_declared_classes(void)
 *   Returns an array with the name of the defined classes
 * Parameters
 *  None
 * Return
 *   Returns an array of the names of the declared classes
 *   in the current script. 
 * Note:
 *   NULL is returned on failure.
 */
static int vm_builtin_get_declared_classes(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_value *pName,*pArray;
	SyHashEntry *pEntry;
	/* Create a new array first */
	pArray = bwa_context_new_array(pCtx);
	pName = bwa_context_new_scalar(pCtx);
	if( pArray == 0 || pName == 0){
		SXUNUSED(nArg); /* cc warning */
		SXUNUSED(apArg);
		/* Out of memory,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Fill the array with the defined classes */
	SyHashResetLoopCursor(&pCtx->pVm->hClass);
	while((pEntry = SyHashGetNextEntry(&pCtx->pVm->hClass)) != 0 ){
		bwa_class *pClass = (bwa_class *)pEntry->pUserData;
		/* Do not register classes defined as interfaces */
		if( (pClass->iFlags & BWA_CLASS_INTERFACE) == 0 ){
			bwa_value_string(pName,SyStringData(&pClass->sName),(int)SyStringLength(&pClass->sName));
			/* insert class name */
			bwa_array_add_elem(pArray,0/*Automatic index assign*/,pName); /* Will make it's own copy */
			/* Reset the cursor */
			bwa_value_reset_string_cursor(pName);
		}
	}
	/* Return the created array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
