/*
 * int/bool assert_options(int $what [, mixed $value ])
 *  Set/get the various assert flags.
 * Parameter
 * $what
 *   ASSERT_ACTIVE          Enable assert() evaluation
 *   ASSERT_WARNING         Issue a warning for each failed assertion
 *   ASSERT_BAIL            Terminate execution on failed assertions
 *   ASSERT_QUIET_EVAL      Not used
 *   ASSERT_CALLBACK        Callback to call on failed assertions
 * $value
 *   An optional new value for the option.
 * Return
 *  Old setting on success or FALSE on failure. 
 */
static int vm_builtin_assert_options(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	int iOld,iNew,iValue; 
	if( nArg < 1 || !bwa_value_is_int(apArg[0]) ){
		/* Missing/Invalid arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Save old assertion flags */
	iOld = pVm->iAssertFlags;
	/* Extract the new flags */
	iNew = bwa_value_to_int(apArg[0]);
	if( iNew == BWA_ASSERT_DISABLE ){
		pVm->iAssertFlags &= ~BWA_ASSERT_DISABLE;
		if( nArg > 1 ){
			iValue = !bwa_value_to_bool(apArg[1]);
			if( iValue ){
				/* Disable assertion */
				pVm->iAssertFlags |= BWA_ASSERT_DISABLE;
			}
		}
	}else if( iNew == BWA_ASSERT_WARNING ){
		pVm->iAssertFlags &= ~BWA_ASSERT_WARNING;
		if( nArg > 1 ){
			iValue = bwa_value_to_bool(apArg[1]);
			if( iValue ){
				/* Issue a warning for each failed assertion */
				pVm->iAssertFlags |= BWA_ASSERT_WARNING;
			}
		}
	}else if( iNew == BWA_ASSERT_BAIL ){
		pVm->iAssertFlags &= ~BWA_ASSERT_BAIL;
		if( nArg > 1 ){
			iValue = bwa_value_to_bool(apArg[1]);
			if( iValue ){
				/* Terminate execution on failed assertions */
				pVm->iAssertFlags |= BWA_ASSERT_BAIL;
			}
		}
	}else if( iNew == BWA_ASSERT_CALLBACK ){
		pVm->iAssertFlags &= ~BWA_ASSERT_CALLBACK;
		if( nArg > 1 && bwa_value_is_callable(apArg[1]) ){
			/* Callback to call on failed assertions */
			BWA_MemObjStore(apArg[1],&pVm->sAssertCallback);
			pVm->iAssertFlags |= BWA_ASSERT_CALLBACK;
		}
	}
	/* Return the old flags */
	bwa_result_int(pCtx,iOld);
	return BWA_OK;
}
/*
 * bool assert(mixed $assertion)
 *  Checks if assertion is FALSE.
 * Parameter
 *  $assertion
 *    The assertion to test.
 * Return
 *  FALSE if the assertion is false, TRUE otherwise.
 */
static int vm_builtin_assert(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pAssert;
	int iFlags,iResult; 
	if( nArg < 1 ){
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	iFlags = pVm->iAssertFlags;
	if( iFlags & BWA_ASSERT_DISABLE ){
		/* Assertion is disabled,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	pAssert = apArg[0];
	iResult = 1; /* cc warning */
	if( pAssert->iFlags & MEMOBJ_STRING ){
		SyString sChunk;
		SyStringInitFromBuf(&sChunk,SyBlobData(&pAssert->sBlob),SyBlobLength(&pAssert->sBlob));
		if( sChunk.nByte > 0 ){
			VmEvalChunk(pVm,pCtx,&sChunk,BWA_PHP_ONLY|BWA_PHP_EXPR,FALSE);
			/* Extract evaluation result */
			iResult = bwa_value_to_bool(pCtx->pRet);
		}else{
			iResult = 0;
		}
	}else{
		/* Perform a boolean cast */
		iResult = bwa_value_to_bool(apArg[0]);
	}
	if( !iResult ){
		/* Assertion failed */
		if( iFlags & BWA_ASSERT_CALLBACK ){
			static const SyString sFileName = { ":Memory", sizeof(":Memory") - 1};
			bwa_value sFile,sLine;
			bwa_value *apCbArg[3];
			SyString *pFile;
			/* Extract the processed script */
			pFile = (SyString *)SySetPeek(&pVm->aFiles);
			if( pFile == 0 ){
				pFile = (SyString *)&sFileName;
			}
			/* Invoke the callback */
			BWA_MemObjInitFromString(pVm,&sFile,pFile);
			BWA_MemObjInitFromInt(pVm,&sLine,0);
			apCbArg[0] = &sFile;
			apCbArg[1] = &sLine;
			apCbArg[2] = pAssert;
			BWA_VmCallUserFunction(pVm,&pVm->sAssertCallback,3,apCbArg,0);
			/* Clean-up the mess left behind */
			BWA_MemObjRelease(&sFile);
			BWA_MemObjRelease(&sLine);
		}
		if( iFlags & BWA_ASSERT_WARNING ){
			/* Emit a warning */
			bwa_context_throw_error(pCtx,BWA_CTX_WARNING,"Assertion failed");
		}
		if( iFlags & BWA_ASSERT_BAIL ){
			/* Abort VM execution immediately */
			return BWA_ABORT;
		}
	}
	/* Assertion result */
	bwa_result_bool(pCtx,iResult);
	return BWA_OK;
}

/*
 * bool trigger_error(string $error_msg[,int $error_type = E_USER_NOTICE ])
 *  Generates a user-level error/warning/notice message.
 * Parameters
 *  $error_msg
 *   The designated error message for this error. It's limited to 1024 characters
 *   in length. Any additional characters beyond 1024 will be truncated.
 * $error_type
 *  The designated error type for this error. It only works with the E_USER family
 *  of constants, and will default to E_USER_NOTICE.
 * Return
 *  This function returns FALSE if wrong error_type is specified, TRUE otherwise.
 */
static int vm_builtin_trigger_error(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	int nErr = BWA_CTX_NOTICE;
	int rc = BWA_OK;
	if( nArg > 0 ){
		const char *zErr;
		int nLen;
		/* Extract the error message */
		zErr = bwa_value_to_string(apArg[0],&nLen);
		if( nArg > 1 ){
			/* Extract the error type */
			nErr = bwa_value_to_int(apArg[1]);
			switch( nErr ){
			case 1:   /* E_ERROR */
			case 16:  /* E_CORE_ERROR */
			case 64:  /* E_COMPILE_ERROR */
			case 256: /* E_USER_ERROR */
				nErr = BWA_CTX_ERR;
				rc = BWA_ABORT; /* Abort processing immediately */
				break;
			case 2:   /* E_WARNING */
			case 32:  /* E_CORE_WARNING */
			case 123: /* E_COMPILE_WARNING */
			case 512: /* E_USER_WARNING */
				nErr = BWA_CTX_WARNING;
				break;
			default:
				nErr = BWA_CTX_NOTICE;
				break;
			}
		}
		/* Report error */
		bwa_context_throw_error_format(pCtx,nErr,"%.*s",nLen,zErr);
		/* Return true */
		bwa_result_bool(pCtx,1);
	}else{
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
	}
	return rc;
}
/*
 * int error_reporting([int $level])
 *  Sets which PHP errors are reported.
 * Parameters
 *  $level
 *   The new error_reporting level. It takes on either a bitmask, or named constants.
 *   Using named constants is strongly encouraged to ensure compatibility for future versions.
 *   As error levels are added, the range of integers increases, so older integer-based error
 *   levels will not always behave as expected.
 *   The available error level constants and the actual meanings of these error levels are described
 *   in the predefined constants.
 * Return
 *   Returns the old error_reporting level or the current level if no level
 *   parameter is given. 
 */
static int vm_builtin_error_reporting(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	int nOld;
	/* Extract the old reporting level */
	nOld = pVm->bErrReport ? 32767 /* E_ALL */ : 0;
	if( nArg > 0 ){
		int nNew;
		/* Extract the desired error reporting level */
		nNew = bwa_value_to_int(apArg[0]);
		if( !nNew ){
			/* Do not report errors at all */
			pVm->bErrReport = 0;
		}else{
			/* Report all errors */
			pVm->bErrReport = 1;
		}
	}
	/* Return the old level */
	bwa_result_int(pCtx,nOld);
	return BWA_OK;
}
/*
 * bool error_log(string $message[,int $message_type = 0 [,string $destination[,string $extra_headers]]])
 *  Send an error message somewhere.
 * Parameter
 *  $message
 *   The error message that should be logged.
 *  $message_type
 *   Says where the error should go. The possible message types are as follows:
 *    0  message is sent to PHP's system logger, using the Operating System's system logging mechanism
 *       or a file, depending on what the error_log configuration directive is set to.
 *       This is the default option.
 *    1 message is sent by email to the address in the destination parameter.
 *      This is the only message type where the fourth parameter, extra_headers is used.
 *    2  No longer an option.
 *    3  message is appended to the file destination. A newline is not automatically added
 *       to the end of the message string.
 *    4  message is sent directly to the SAPI logging handler.
 *  $destination
 *   The destination. Its meaning depends on the message_type parameter as described above.
 *  $extra_headers
 *   The extra headers. It's used when the message_type parameter is set to 1
 * Return
 *  TRUE on success or FALSE on failure. 
 * NOTE:
 *  Actually,BWA does not care about the given parameters,all this function does
 *  is to invoke any user callback registered using the BWA_VM_CONFIG_ERR_LOG_HANDLER
 *  configuration directive (refer to the official documentation for more information).
 *  Otherwise this function is no-op.
 */
static int vm_builtin_error_log(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zMessage,*zDest,*zHeader;
	bwa_vm *pVm = pCtx->pVm;
	int iType = 0;
	if( nArg < 1 ){
		/* Missing log message,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	if( pVm->xErrLog  ){
		/* Invoke the user callback */
		zMessage = bwa_value_to_string(apArg[0],0);
		zDest = zHeader = ""; /* Empty string */
		if( nArg > 1 ){
			iType = bwa_value_to_int(apArg[1]);
			if( nArg > 2 ){
				zDest = bwa_value_to_string(apArg[2],0);
				if( nArg > 3 ){
					zHeader = bwa_value_to_string(apArg[3],0);
				}
			}
		}
		pVm->xErrLog(zMessage,iType,zDest,zHeader);
	}
	/* Retun TRUE */
	bwa_result_bool(pCtx,1);
	return BWA_OK;
}
/*
 * bool restore_exception_handler(void) 
 *  Restores the previously defined exception handler function.
 * Parameter
 *  None
 * Return
 *  TRUE if the exception handler is restored.FALSE otherwise
 */
static int vm_builtin_restore_exception_handler(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pOld,*pNew;
	/* Point to the old and the new handler */
	pOld = &pVm->aExceptionCB[0];
	pNew = &pVm->aExceptionCB[1];
	if( pOld->iFlags & MEMOBJ_NULL ){
		SXUNUSED(nArg); /* cc warning */
		SXUNUSED(apArg);
		/* No installed handler,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Copy the old handler */
	BWA_MemObjStore(pOld,pNew);
	BWA_MemObjRelease(pOld);
	/* Return TRUE */
	bwa_result_bool(pCtx,1);
	return BWA_OK;
}
/*
 * callable set_exception_handler(callable $exception_handler)
 *  Sets a user-defined exception handler function.
 *  Sets the default exception handler if an exception is not caught within a try/catch block.
 * NOTE
 *  Execution will NOT stop after the exception_handler calls for example die/exit unlike
 *  the satndard PHP engine.
 * Parameters
 *  $exception_handler
 *   Name of the function to be called when an uncaught exception occurs.
 *   This handler function needs to accept one parameter, which will be the exception object
 *   that was thrown.
 *  Note:
 *   NULL may be passed instead, to reset this handler to its default state. 
 * Return
 *  Returns the name of the previously defined exception handler, or NULL on error.
 *  If no previous handler was defined, NULL is also returned. If NULL is passed
 *  resetting the handler to its default state, TRUE is returned. 
 */
static int vm_builtin_set_exception_handler(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pOld,*pNew;
	/* Point to the old and the new handler */
	pOld = &pVm->aExceptionCB[0];
	pNew = &pVm->aExceptionCB[1];
	/* Return the old handler */
	bwa_result_value(pCtx,pOld); /* Will make it's own copy */
	if( nArg > 0 ){
		if( !bwa_value_is_callable(apArg[0])) {
			/* Not callable,return TRUE (As requested by the PHP specification) */
			BWA_MemObjRelease(pNew);
			bwa_result_bool(pCtx,1);
		}else{
			BWA_MemObjStore(pNew,pOld);
			/* Install the new handler */
			BWA_MemObjStore(apArg[0],pNew);
		}
	}
	return BWA_OK;
}
/*
 * bool restore_error_handler(void) 
 *  THIS FUNCTION IS A NO-OP IN THE CURRENT RELEASE OF THE BWA ENGINE.
 * Parameters:
 *  None.
 * Return
 *  Always TRUE.
 */
static int vm_builtin_restore_error_handler(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pOld,*pNew;
	/* Point to the old and the new handler */
	pOld = &pVm->aErrCB[0];
	pNew = &pVm->aErrCB[1];
	if( pOld->iFlags & MEMOBJ_NULL ){
		SXUNUSED(nArg); /* cc warning */
		SXUNUSED(apArg);
		/* No installed callback,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Copy the old callback */
	BWA_MemObjStore(pOld,pNew);
	BWA_MemObjRelease(pOld);
	/* Return TRUE */
	bwa_result_bool(pCtx,1);
	return BWA_OK;
}
/*
 * value set_error_handler(callable $error_handler)
 *  +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
 *   THIS FUNCTION IS DISABLED IN THE CURRENT RELEASE OF THE BWA ENGINE.
 *  +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
 *  Sets a user-defined error handler function.
 *  This function can be used for defining your own way of handling errors during
 *  runtime, for example in applications in which you need to do cleanup of data/files
 *  when a critical error happens, or when you need to trigger an error under certain 
 *  conditions (using trigger_error()). 
 * Parameters
 *  $error_handler
 *   The user function needs to accept two parameters: the error code, and a string
 *   describing the error.
 *   Then there are three optional parameters that may be supplied: the filename in which
 *   the error occurred, the line number in which the error occurred, and the context in which
 *   the error occurred (an array that points to the active symbol table at the point the error occurred).
 *   The function can be shown as:
 *    handler ( int $errno , string $errstr [, string $errfile])
 *     errno
 *       The first parameter, errno, contains the level of the error raised, as an integer. 
 *   errstr
 *      The second parameter, errstr, contains the error message, as a string. 
 *   errfile
 *      The third parameter is optional, errfile, which contains the filename that the error
 *     was raised in, as a string. 
 *  Note:
 *   NULL may be passed instead, to reset this handler to its default state. 
 * Return
 *  Returns the name of the previously defined error handler, or NULL on error.
 *  If no previous handler was defined, NULL is also returned. If NULL is passed
 *  resetting the handler to its default state, TRUE is returned. 
 */
static int vm_builtin_set_error_handler(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pOld,*pNew;
	/* Point to the old and the new handler */
	pOld = &pVm->aErrCB[0];
	pNew = &pVm->aErrCB[1];
	/* Return the old handler */
	bwa_result_value(pCtx,pOld); /* Will make it's own copy */
	if( nArg > 0 ){
		if( !bwa_value_is_callable(apArg[0])) {
			/* Not callable,return TRUE (As requested by the PHP specification) */
			BWA_MemObjRelease(pNew);
			bwa_result_bool(pCtx,1);
		}else{
			BWA_MemObjStore(pNew,pOld);
			/* Install the new handler */
			BWA_MemObjStore(apArg[0],pNew);
		}
	}
	bwa_context_throw_error_format(pCtx,BWA_CTX_WARNING,
		"This function is disabled in the current release of the BWA(%s) engine",
		bwa_lib_version()
		);
	return BWA_OK;
}
/*
 * array debug_backtrace([ int $options = DEBUG_BACKTRACE_PROVIDE_OBJECT [, int $limit = 0 ]] )
 *  Generates a backtrace.
 * Paramaeter
 *  $options
 *   DEBUG_BACKTRACE_PROVIDE_OBJECT: Whether or not to populate the "object" index.
 *   DEBUG_BACKTRACE_IGNORE_ARGS 	Whether or not to omit the "args" index, and thus
 *   all the function/method arguments, to save memory.
 * $limit
 *   (Not Used)
 * Return
 *  An array.The possible returned elements are as follows:
 *          Possible returned elements from debug_backtrace()
 *          Name        Type      Description
 *          ------      ------     -----------
 *          function    string    The current function name. See also __FUNCTION__.
 *          line        integer   The current line number. See also __LINE__.
 *          file 	    string 	  The current file name. See also __FILE__.
 *          class       string    The current class name. See also __CLASS__
 *          object      object    The current object.
 *          args        array     If inside a function, this lists the functions arguments.
 *                                If inside an included file, this lists the included file name(s). 
 */
static int vm_builtin_debug_backtrace(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	bwa_value *pArray;
	bwa_class *pClass;
	bwa_value *pValue;
	SyString *pFile;
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	pValue = bwa_context_new_scalar(pCtx);
	if( pArray == 0 || pValue == 0 ){
		/* Out of memory,return NULL */
		bwa_context_throw_error(pCtx,BWA_CTX_ERR,"BWA is running out of memory");
		bwa_result_null(pCtx);
		SXUNUSED(nArg); /* cc warning */
		SXUNUSED(apArg);
		return BWA_OK;
	}
	/* Dump running function name and it's arguments  */
	if( pVm->pFrame->pParent ){
		VmFrame *pFrame = pVm->pFrame;
		bwa_vm_func *pFunc;
		bwa_value *pArg;
		while( pFrame->pParent && (pFrame->iFlags & VM_FRAME_EXCEPTION) ){
			/* Safely ignore the exception frame */
			pFrame = pFrame->pParent;
		}
		pFunc = (bwa_vm_func *)pFrame->pUserData;
		if( pFrame->pParent && pFunc ){
			bwa_value_string(pValue,pFunc->sName.zString,(int)pFunc->sName.nByte);
			bwa_array_add_strkey_elem(pArray,"function",pValue);
			bwa_value_reset_string_cursor(pValue);
		}
		/* Function arguments */
		pArg = bwa_context_new_array(pCtx);
		if( pArg  ){
			bwa_value *pObj;
			VmSlot *aSlot;
			sxu32 n;
			/* Start filling the array with the given arguments */
			aSlot = (VmSlot *)SySetBasePtr(&pFrame->sArg);
			for( n = 0;  n < SySetUsed(&pFrame->sArg) ; n++ ){
				pObj = (bwa_value *)SySetAt(&pCtx->pVm->aMemObj,aSlot[n].nIdx);
				if( pObj ){
					bwa_array_add_elem(pArg,0/* Automatic index assign*/,pObj);
				}
			}
			/* Save the array */
			bwa_array_add_strkey_elem(pArray,"args",pArg);
		}
	}
	bwa_value_int(pValue,1);
	/* Append the current line (which is always 1 since BWA does not track
	 * line numbers at run-time. )
	 */
	bwa_array_add_strkey_elem(pArray,"line",pValue);
	/* Current processed script */
	pFile = (SyString *)SySetPeek(&pVm->aFiles);
	if( pFile ){
		bwa_value_string(pValue,pFile->zString,(int)pFile->nByte);
		bwa_array_add_strkey_elem(pArray,"file",pValue);
		bwa_value_reset_string_cursor(pValue);
	}
	/* Top class */
	pClass = BWA_VmPeekTopClass(pVm);
	if( pClass ){
		bwa_value_reset_string_cursor(pValue);
		bwa_value_string(pValue,pClass->sName.zString,(int)pClass->sName.nByte);
		bwa_array_add_strkey_elem(pArray,"class",pValue);
	}
	/* Return the freshly created array */
	bwa_result_value(pCtx,pArray);
	/*
	 * Don't worry about freeing memory, everything will be released automatically
	 * as soon we return from this function.
	 */
	return BWA_OK;
}
/* 
 * Generate a small backtrace.
 * Store the generated dump in the given BLOB
 */
static int VmMiniBacktrace(
	bwa_vm *pVm, /* Target VM */
	SyBlob *pOut /* Store Dump here */
	)
{
	VmFrame *pFrame = pVm->pFrame;
	bwa_vm_func *pFunc;
	bwa_class *pClass;
	SyString *pFile;
	/* Called function */
	while( pFrame->pParent && (pFrame->iFlags & VM_FRAME_EXCEPTION) ){
		/* Safely ignore the exception frame */
		pFrame = pFrame->pParent;
	}
	pFunc = (bwa_vm_func *)pFrame->pUserData;
	SyBlobAppend(pOut,"[",sizeof(char));
	if( pFrame->pParent && pFunc ){
		SyBlobAppend(pOut,"Called function: ",sizeof("Called function: ")-1);
		SyBlobAppend(pOut,pFunc->sName.zString,pFunc->sName.nByte);
	}else{
		SyBlobAppend(pOut,"Global scope",sizeof("Global scope") - 1);
	}
	SyBlobAppend(pOut,"]",sizeof(char));
	/* Current processed script */
	pFile = (SyString *)SySetPeek(&pVm->aFiles);
	if( pFile ){
		SyBlobAppend(pOut,"[",sizeof(char));
		SyBlobAppend(pOut,"Processed file: ",sizeof("Processed file: ")-1);
		SyBlobAppend(pOut,pFile->zString,pFile->nByte);
		SyBlobAppend(pOut,"]",sizeof(char));
	}
	/* Top class */
	pClass = BWA_VmPeekTopClass(pVm);
	if( pClass ){
		SyBlobAppend(pOut,"[",sizeof(char));
		SyBlobAppend(pOut,"Class: ",sizeof("Class: ")-1);
		SyBlobAppend(pOut,pClass->sName.zString,pClass->sName.nByte);
		SyBlobAppend(pOut,"]",sizeof(char));
	}
	SyBlobAppend(pOut,"\n",sizeof(char));
	/* All done */
	return SXRET_OK;
}
/*
 * void debug_print_backtrace()
 *  Prints a backtrace
 * Parameters
 * None
 * Return
 * NULL
 */
static int vm_builtin_debug_print_backtrace(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	SyBlob sDump;
	SyBlobInit(&sDump,&pVm->sAllocator);
	/* Generate the backtrace */
	VmMiniBacktrace(pVm,&sDump);
	/* Output backtrace */
	bwa_context_output(pCtx,(const char *)SyBlobData(&sDump),(int)SyBlobLength(&sDump));
	/* All done,cleanup */
	SyBlobRelease(&sDump);
	SXUNUSED(nArg); /* cc warning */
	SXUNUSED(apArg);
	return BWA_OK;
}
/*
 * string debug_string_backtrace()
 *  Generate a backtrace
 * Parameters
 * None
 * Return
 *  A mini backtrace().
 * Note that this is a symisc extension.
 */
static int vm_builtin_debug_string_backtrace(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_vm *pVm = pCtx->pVm;
	SyBlob sDump;
	SyBlobInit(&sDump,&pVm->sAllocator);
	/* Generate the backtrace */
	VmMiniBacktrace(pVm,&sDump);
	/* Return the backtrace */
	bwa_result_string(pCtx,(const char *)SyBlobData(&sDump),(int)SyBlobLength(&sDump)); /* Will make it's own copy */
	/* All done,cleanup */
	SyBlobRelease(&sDump);
	SXUNUSED(nArg); /* cc warning */
	SXUNUSED(apArg);
	return BWA_OK;
}
/*
 * The following routine is invoked by the engine when an uncaught
 * exception is triggered.
 */
static sxi32 VmUncaughtException(
	bwa_vm *pVm, /* Target VM */
	bwa_class_instance *pThis /* Exception class instance [i.e: Exception $e] */
	)
{
	bwa_value *apArg[2],sArg;
	int nArg = 1;
	sxi32 rc;
	if( pVm->nExceptDepth > 15 ){
		/* Nesting limit reached */
		return SXRET_OK;
	}
	/* Call any exception handler if available */
	BWA_MemObjInit(pVm,&sArg);
	if( pThis ){
		/* Load the exception instance */
		sArg.x.pOther = pThis;
		pThis->iRef++;
		MemObjSetType(&sArg,MEMOBJ_OBJ);
	}else{
		nArg = 0;
	}
	apArg[0] = &sArg;
	/* Call the exception handler if available */
	pVm->nExceptDepth++;
	rc = BWA_VmCallUserFunction(&(*pVm),&pVm->aExceptionCB[1],1,apArg,0);
	pVm->nExceptDepth--;
	if( rc != SXRET_OK ){
		SyString sName = { "Exception" , sizeof("Exception") - 1 };
		SyString sFuncName = { "Global",sizeof("Global") - 1 };
		VmFrame *pFrame = pVm->pFrame;
		/* No available handler,generate a fatal error */ 
		if( pThis ){
			SyStringDupPtr(&sName,&pThis->pClass->sName);
		}
		while( pFrame->pParent && (pFrame->iFlags & VM_FRAME_EXCEPTION) ){
			/* Ignore exception frames */
			pFrame = pFrame->pParent;
		}
		if( pFrame->pParent ){
			if( pFrame->iFlags & VM_FRAME_CATCH ){
				SyStringInitFromBuf(&sFuncName,"Catch_block",sizeof("Catch_block")-1);
			}else{
				bwa_vm_func *pFunc = (bwa_vm_func *)pFrame->pUserData;
				if( pFunc ){
					SyStringDupPtr(&sFuncName,&pFunc->sName);
				}
			}
		}
		/* Generate a listing */
		VmErrorFormat(&(*pVm),BWA_CTX_ERR,
			"Uncaught exception '%z' in the '%z' frame context",
			&sName,&sFuncName);
		/* Tell the upper layer to stop VM execution immediately  */
		rc = SXERR_ABORT;
	}
	BWA_MemObjRelease(&sArg);
	return rc;
}
/*
 * Throw an user exception.
 */
static sxi32 VmThrowException(
	bwa_vm *pVm,              /* Target VM */
	bwa_class_instance *pThis /* Exception class instance [i.e: Exception $e] */
	)
{
	bwa_exception_block *pCatch; /* Catch block to execute */
	bwa_exception **apException;
	bwa_exception *pException;
	/* Point to the stack of loaded exceptions */
	apException = (bwa_exception **)SySetBasePtr(&pVm->aException);
	pException = 0;
	pCatch = 0;
	if( SySetUsed(&pVm->aException) > 0 ){
		bwa_exception_block *aCatch;
		bwa_class *pClass;
		sxu32 j;
		/* Locate the appropriate block to execute */
		pException = apException[SySetUsed(&pVm->aException) - 1];
		(void)SySetPop(&pVm->aException);
		aCatch = (bwa_exception_block *)SySetBasePtr(&pException->sEntry);
		for( j = 0 ; j < SySetUsed(&pException->sEntry) ; ++j ){
			SyString *pName = &aCatch[j].sClass;
			/* Extract the target class */
			pClass = BWA_VmExtractClass(&(*pVm),pName->zString,pName->nByte,TRUE,0);
			if( pClass == 0 ){
				/* No such class */
				continue;
			}
			if( VmInstanceOf(pThis->pClass,pClass) ){
				/* Catch block found,break immeditaley */
				pCatch = &aCatch[j];
				break;
			}
		}		
	}
	/* Execute the cached block if available */
	if( pCatch == 0 ){
		sxi32 rc;
		rc = VmUncaughtException(&(*pVm),pThis);
		if( rc == SXRET_OK && pException ){
			VmFrame *pFrame = pVm->pFrame;
			while( pFrame->pParent && (pFrame->iFlags & VM_FRAME_EXCEPTION) ){
				/* Safely ignore the exception frame */
				pFrame = pFrame->pParent;
			}
			if( pException->pFrame == pFrame ){
				/* Tell the upper layer that the exception was caught */
				pFrame->iFlags &= ~VM_FRAME_THROW;
			}
		}
		return rc;
	}else{
		VmFrame *pFrame = pVm->pFrame;
		sxi32 rc;
		while( pFrame->pParent && (pFrame->iFlags & VM_FRAME_EXCEPTION) ){
			/* Safely ignore the exception frame */
			pFrame = pFrame->pParent;
		}
		if( pException->pFrame == pFrame ){
			/* Tell the upper layer that the exception was caught */
			pFrame->iFlags &= ~VM_FRAME_THROW;
		}
		/* Create a private frame first */
		rc = VmEnterFrame(&(*pVm),0,0,&pFrame);
		if( rc == SXRET_OK ){
			/* Mark as catch frame */
			bwa_value *pObj = VmExtractMemObj(&(*pVm),&pCatch->sThis,FALSE,TRUE);
			pFrame->iFlags |= VM_FRAME_CATCH;
			if( pObj ){
				/* Install the exception instance */
				pThis->iRef++; /* Increment reference count */
				pObj->x.pOther = pThis;
				MemObjSetType(pObj,MEMOBJ_OBJ);
			}
			/* Exceute the block */
			VmLocalExec(&(*pVm),&pCatch->sByteCode,0);
			/* Leave the frame */
			VmLeaveFrame(&(*pVm));
		}		
	}
	/* TICKET 1433-60: Do not release the 'pException' pointer since it may
	 * be used again if a 'goto' statement is executed.
	 */
	return SXRET_OK;
}

