/*
 * XML parser event callbacks
 * Each time the unserlying XML parser extract a single token
 * from the input,one of the following callbacks are invoked.
 * IMP-XML-ENGINE-07-07-2012 22:02 FreeBSD [chm@symisc.net]
 */
/*
 * Create a scalar bwa_value holding the value
 * of an XML tag/attribute/CDATA and so on.
 */
static bwa_value * VmXMLValue(bwa_xml_engine *pEngine,SyXMLRawStr *pXML,SyXMLRawStr *pNsUri)
{
	bwa_value *pValue;
	/* Allocate a new scalar variable */
	pValue = bwa_context_new_scalar(pEngine->pCtx);
	if( pValue == 0 ){
		bwa_context_throw_error(pEngine->pCtx,BWA_CTX_ERR,"BWA is running out of memory");
		return 0;
	}
	if( pNsUri && pNsUri->nByte > 0 ){
		/* Append namespace URI and the separator */
		bwa_value_string_format(pValue,"%.*s%c",pNsUri->nByte,pNsUri->zString,pEngine->ns_sep);
	}
	/* Copy the tag value */
	bwa_value_string(pValue,pXML->zString,(int)pXML->nByte);
	return pValue;
}
/*
 * Create a 'bwa_value' of type array holding the values
 * of an XML tag attributes.
 */
static bwa_value * VmXMLAttrValue(bwa_xml_engine *pEngine,SyXMLRawStr *aAttr,sxu32 nAttr)
{
	bwa_value *pArray;
	/* Create an empty array */
	pArray = bwa_context_new_array(pEngine->pCtx);
	if( pArray == 0 ){
		bwa_context_throw_error(pEngine->pCtx,BWA_CTX_ERR,"BWA is running out of memory");
		return 0;
	}
	if( nAttr > 0 ){
		bwa_value *pKey,*pValue;
		sxu32 n;
		/* Create worker variables */
		pKey = bwa_context_new_scalar(pEngine->pCtx);
		pValue = bwa_context_new_scalar(pEngine->pCtx);
		if( pKey == 0 || pValue == 0 ){
			bwa_context_throw_error(pEngine->pCtx,BWA_CTX_ERR,"BWA is running out of memory");
			return 0;
		}
		/* Copy attributes */
		for( n = 0 ; n < nAttr ; n += 2 ){
			/* Reset string cursors */
			bwa_value_reset_string_cursor(pKey);
			bwa_value_reset_string_cursor(pValue);
			/* Copy attribute name and it's associated value */
			bwa_value_string(pKey,aAttr[n].zString,(int)aAttr[n].nByte); /* Attribute name */
			bwa_value_string(pValue,aAttr[n+1].zString,(int)aAttr[n+1].nByte); /* Attribute value */
			/* Insert in the array */
			bwa_array_add_elem(pArray,pKey,pValue); /* Will make it's own copy */
		}
		/* Release the worker variables */
		bwa_context_release_value(pEngine->pCtx,pKey);
		bwa_context_release_value(pEngine->pCtx,pValue);
	}
	/* Return the freshly created array */
	return pArray;
}
/*
 * Start element handler.
 * The user defined callback must accept three parameters:
 *    start_element_handler(resource $parser,string $name,array $attribs )
 *    $parser
 *      The first parameter, parser, is a reference to the XML parser calling the handler. 
 *    $name
 *      The second parameter, name, contains the name of the element for which this handler
 *		is called.If case-folding is in effect for this parser, the element name will be in uppercase letters. 
 *    $attribs
 *      The third parameter, attribs, contains an associative array with the element's attributes (if any).
 *		The keys of this array are the attribute names, the values are the attribute values.
 *      Attribute names are case-folded on the same criteria as element names.Attribute values are not case-folded.
 *      The original order of the attributes can be retrieved by walking through attribs the normal way, using each().
 *      The first key in the array was the first attribute, and so on. 
 *      Note: Instead of a function name, an array containing an object reference and a method name can also be supplied.
 */
static sxi32 VmXMLStartElementHandler(SyXMLRawStr *pStart,SyXMLRawStr *pNS,sxu32 nAttr,SyXMLRawStr *aAttr,void *pUserData)
{
	bwa_xml_engine *pEngine = (bwa_xml_engine *)pUserData;
	bwa_value *pCallback,*pTag,*pAttr;
	/* Point to the target user defined callback */
	pCallback = &pEngine->aCB[BWA_XML_START_TAG];
	/* Make sure the given callback is callable */
	if( !BWA_VmIsCallable(pEngine->pVm,pCallback,0) ){
		/* Not callable,return immediately*/
		return SXRET_OK;
	}
	/* Create a bwa_value holding the tag name */
	pTag = VmXMLValue(pEngine,pStart,pNS);
	/* Create a bwa_value holding the tag attributes */
	pAttr = VmXMLAttrValue(pEngine,aAttr,nAttr);
	if( pTag == 0  || pAttr == 0 ){
		SXUNUSED(pNS); /* cc warning */
		/* Out of mem,return immediately */
		return SXRET_OK;
	}
	/* Invoke the user callback */
	BWA_VmCallUserFunctionAp(pEngine->pVm,pCallback,0,&pEngine->sParserValue,pTag,pAttr,0);
	/* Clean-up the mess left behind */
	bwa_context_release_value(pEngine->pCtx,pTag);
	bwa_context_release_value(pEngine->pCtx,pAttr);
	return SXRET_OK;
}
