/*
 * mixed xml_parser_get_option(resource $parser,int $option)
 *  Get options from an XML parser.
 * Parameters
 *  $parser
 *   A reference to the XML parser to set an option in.
 * $option
 *   Which option to fetch.
 * Return
 *  This function returns FALSE if parser does not refer to a valid parser
 *  or if option isn't valid.Else the option's value is returned.
 */
static int vm_builtin_xml_parser_get_option(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_xml_engine *pEngine;
	int nOp;
	if( nArg < 2 || !bwa_value_is_resource(apArg[0]) ){
		/* Missing/Ivalid argument,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the XML engine */
	pEngine = (bwa_xml_engine *)bwa_value_to_resource(apArg[0]);
	if( IS_INVALID_XML_ENGINE(pEngine) ){
		/* Corrupt engine,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the option */
	nOp = bwa_value_to_int(apArg[1]);
	switch(nOp){
	case SXML_OPTION_SKIP_TAGSTART:
	case SXML_OPTION_SKIP_WHITE:
	case SXML_OPTION_CASE_FOLDING:
		bwa_result_int(pCtx,0); break;
	case SXML_OPTION_TARGET_ENCODING:
		bwa_result_string(pCtx,"UTF-8",(int)sizeof("UTF-8")-1);
		break;
	default:
		/* Unknown option,return FALSE*/
		bwa_result_bool(pCtx,0);
		break;
	}
	return BWA_OK;
}
/*
 * string xml_error_string(int $code)
 *  Gets the XML parser error string associated with the given code.
 * Parameters
 *  $code
 *   An error code from xml_get_error_code().
 * Return
 *  Returns a string with a textual description of the error 
 *  code, or FALSE if no description was found.
 */
static int vm_builtin_xml_error_string(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	int nErr = -1;
	if( nArg > 0 ){
		nErr = bwa_value_to_int(apArg[0]);
	}
	switch(nErr){
	case SXML_ERROR_DUPLICATE_ATTRIBUTE:
		bwa_result_string(pCtx,"Duplicate attribute",-1/*Compute length automatically*/);
		break;
	case SXML_ERROR_INCORRECT_ENCODING:
		bwa_result_string(pCtx,"Incorrect encoding",-1);
		break;
	case SXML_ERROR_INVALID_TOKEN:
		bwa_result_string(pCtx,"Unexpected token",-1);
		break;
	case SXML_ERROR_MISPLACED_XML_PI:
		bwa_result_string(pCtx,"Misplaced processing instruction",-1);
		break;
	case SXML_ERROR_NO_MEMORY:
		bwa_result_string(pCtx,"Out of memory",-1);
		break;
	case SXML_ERROR_NONE:
		bwa_result_string(pCtx,"Not an error",-1);
		break;
	case SXML_ERROR_TAG_MISMATCH:
		bwa_result_string(pCtx,"Tag mismatch",-1);
		break;
	case -1:
		bwa_result_string(pCtx,"Unknown error code",-1);
		break;
	default:
		bwa_result_string(pCtx,"Syntax error",-1);
		break;
	}
	return BWA_OK;
}
