/*
 * int utf8_encode(string $input)
 *  UTF-8 encoding.
 *  This function encodes the string data to UTF-8, and returns the encoded version.
 *  UTF-8 is a standard mechanism used by Unicode for encoding wide character values
 * into a byte stream. UTF-8 is transparent to plain ASCII characters, is self-synchronized
 * (meaning it is possible for a program to figure out where in the bytestream characters start)
 * and can be used with normal string comparison functions for sorting and such.
 *  Notes on UTF-8 (According to SQLite3 authors):
 *  Byte-0    Byte-1    Byte-2    Byte-3    Value
 *  0xxxxxxx                                 00000000 00000000 0xxxxxxx
 *  110yyyyy  10xxxxxx                       00000000 00000yyy yyxxxxxx
 *  1110zzzz  10yyyyyy  10xxxxxx             00000000 zzzzyyyy yyxxxxxx
 *  11110uuu  10uuzzzz  10yyyyyy  10xxxxxx   000uuuuu zzzzyyyy yyxxxxxx
 * Parameters
 * $input
 *   String to encode or NULL on failure.
 * Return
 *  An UTF-8 encoded string.
 */
static int vm_builtin_utf8_encode(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const unsigned char *zIn,*zEnd;
	int nByte,c,e;
	if( nArg < 1 ){
		/* Missing arguments,return null */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Extract the target string */
	zIn = (const unsigned char *)bwa_value_to_string(apArg[0],&nByte);
	if( nByte < 1 ){
		/* Empty string,return null */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	zEnd = &zIn[nByte];
	/* Start the encoding process */
	for(;;){
		if( zIn >= zEnd ){
			/* End of input */
			break;
		}
		c = zIn[0];
		/* Advance the stream cursor */
		zIn++;
		/* Encode */
		if( c<0x00080 ){
			e = (c&0xFF);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
		}else if( c<0x00800 ){
			e = 0xC0 + ((c>>6)&0x1F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + (c & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
		}else if( c<0x10000 ){
			e = 0xE0 + ((c>>12)&0x0F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + ((c>>6) & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + (c & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
		}else{
			e = 0xF0 + ((c>>18) & 0x07);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + ((c>>12) & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + ((c>>6) & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
			e = 0x80 + (c & 0x3F);
			bwa_result_string(pCtx,(const char *)&e,(int)sizeof(char));
		} 
	}
	/* All done */
	return BWA_OK;
}
