/*
 * bool putenv(string $settings)
 *  Set the value of an environment variable.
 * Parameters
 *  $setting
 *   The setting, like "FOO=BAR"
 * Return
 *  TRUE on success or FALSE on failure.  
 */
static int BWA_vfs_putenv(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zName,*zValue;
	char *zSettings,*zEnd;
	bwa_vfs *pVfs;
	int iLen,rc;
	if( nArg < 1 || !bwa_value_is_string(apArg[0]) ){
		/* Missing/Invalid argument,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the setting variable */
	zSettings = (char *)bwa_value_to_string(apArg[0],&iLen);
	if( iLen < 1 ){
		/* Empty string,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Parse the setting */
	zEnd = &zSettings[iLen];
	zValue = 0;
	zName = zSettings;
	while( zSettings < zEnd ){
		if( zSettings[0] == '=' ){
			/* Null terminate the name */
			zSettings[0] = 0;
			zValue = &zSettings[1];
			break;
		}
		zSettings++;
	}
	/* Install the environment variable in the $_Env array */
	if( zValue == 0 || zName[0] == 0 || zValue >= zEnd || zName >= zValue ){
		/* Invalid settings,retun FALSE */
		bwa_result_bool(pCtx,0);
		if( zSettings  < zEnd ){
			zSettings[0] = '=';
		}
		return BWA_OK;
	}
	bwa_vm_config(pCtx->pVm,BWA_VM_CONFIG_ENV_ATTR,zName,zValue,(int)(zEnd-zValue));
	/* Point to the underlying vfs */
	pVfs = (bwa_vfs *)bwa_context_user_data(pCtx);
	if( pVfs == 0 || pVfs->xSetenv == 0 ){
		/* IO routine not implemented,return NULL */
		bwa_context_throw_error_format(pCtx,BWA_CTX_WARNING,
			"IO routine(%s) not implemented in the underlying VFS,BWA is returning FALSE",
			bwa_function_name(pCtx)
			);
		bwa_result_bool(pCtx,0);
		zSettings[0] = '=';
		return BWA_OK;
	}
	/* Perform the requested operation */
	rc = pVfs->xSetenv(zName,zValue);
	bwa_result_bool(pCtx,rc == BWA_OK );
	zSettings[0] = '=';
	return BWA_OK;
}
