/*
 * Convert open modes (string passed to the fopen() function) [i.e: 'r','w+','a',...] into BWA flags.
 * According to the PHP reference manual:
 *  The mode parameter specifies the type of access you require to the stream. It may be any of the following
 *   'r' 	Open for reading only; place the file pointer at the beginning of the file.
 *   'r+' 	Open for reading and writing; place the file pointer at the beginning of the file.
 *   'w' 	Open for writing only; place the file pointer at the beginning of the file and truncate the file
 *          to zero length. If the file does not exist, attempt to create it.
 *   'w+' 	Open for reading and writing; place the file pointer at the beginning of the file and truncate
 *              the file to zero length. If the file does not exist, attempt to create it.
 *   'a' 	Open for writing only; place the file pointer at the end of the file. If the file does not 
 *         exist, attempt to create it.
 *   'a+' 	Open for reading and writing; place the file pointer at the end of the file. If the file does 
 *          not exist, attempt to create it.
 *   'x' 	Create and open for writing only; place the file pointer at the beginning of the file. If the file
 *         already exists,
 *         the fopen() call will fail by returning FALSE and generating an error of level E_WARNING. If the file
 *         does not exist attempt to create it. This is equivalent to specifying O_EXCL|O_CREAT flags for
 *         the underlying open(2) system call.
 *   'x+' 	Create and open for reading and writing; otherwise it has the same behavior as 'x'.
 *   'c' 	Open the file for writing only. If the file does not exist, it is created. If it exists, it is neither truncated
 *          (as opposed to 'w'), nor the call to this function fails (as is the case with 'x'). The file pointer
 *          is positioned on the beginning of the file.
 *          This may be useful if it's desired to get an advisory lock (see flock()) before attempting to modify the file
 *          as using 'w' could truncate the file before the lock was obtained (if truncation is desired, ftruncate() can
 *          be used after the lock is requested).
 *   'c+' 	Open the file for reading and writing; otherwise it has the same behavior as 'c'. 
 */
static int StrModeToFlags(bwa_context *pCtx,const char *zMode,int nLen)
{
	const char *zEnd = &zMode[nLen];
	int iFlag = 0;
	int c;
	if( nLen < 1 ){
		/* Open in a read-only mode */
		return BWA_IO_OPEN_RDONLY;
	}
	c = zMode[0];
	if( c == 'r' || c == 'R' ){
		/* Read-only access */
		iFlag = BWA_IO_OPEN_RDONLY;
		zMode++; /* Advance */
		if( zMode < zEnd ){
			c = zMode[0];
			if( c == '+' || c == 'w' || c == 'W' ){
				/* Read+Write access */
				iFlag = BWA_IO_OPEN_RDWR;
			}
		}
	}else if( c == 'w' || c == 'W' ){
		/* Overwrite mode.
		 * If the file does not exists,try to create it
		 */
		iFlag = BWA_IO_OPEN_WRONLY|BWA_IO_OPEN_TRUNC|BWA_IO_OPEN_CREATE;
		zMode++; /* Advance */
		if( zMode < zEnd ){
			c = zMode[0];
			if( c == '+' || c == 'r' || c == 'R' ){
				/* Read+Write access */
				iFlag &= ~BWA_IO_OPEN_WRONLY;
				iFlag |= BWA_IO_OPEN_RDWR;
			}
		}
	}else if( c == 'a' || c == 'A' ){
		/* Append mode (place the file pointer at the end of the file).
		 * Create the file if it does not exists.
		 */
		iFlag = BWA_IO_OPEN_WRONLY|BWA_IO_OPEN_APPEND|BWA_IO_OPEN_CREATE;
		zMode++; /* Advance */
		if( zMode < zEnd ){
			c = zMode[0];
			if( c == '+' ){
				/* Read-Write access */
				iFlag &= ~BWA_IO_OPEN_WRONLY;
				iFlag |= BWA_IO_OPEN_RDWR;
			}
		}
	}else if( c == 'x' || c == 'X' ){
		/* Exclusive access.
		 * If the file already exists,return immediately with a failure code.
		 * Otherwise create a new file.
		 */
		iFlag = BWA_IO_OPEN_WRONLY|BWA_IO_OPEN_EXCL;
		zMode++; /* Advance */
		if( zMode < zEnd ){
			c = zMode[0];
			if( c == '+' || c == 'r' || c == 'R' ){
				/* Read-Write access */
				iFlag &= ~BWA_IO_OPEN_WRONLY;
				iFlag |= BWA_IO_OPEN_RDWR;
			}
		}
	}else if( c == 'c' || c == 'C' ){
		/* Overwrite mode.Create the file if it does not exists.*/
		iFlag = BWA_IO_OPEN_WRONLY|BWA_IO_OPEN_CREATE;
		zMode++; /* Advance */
		if( zMode < zEnd ){
			c = zMode[0];
			if( c == '+' ){
				/* Read-Write access */
				iFlag &= ~BWA_IO_OPEN_WRONLY;
				iFlag |= BWA_IO_OPEN_RDWR;
			}
		}
	}else{
		/* Invalid mode. Assume a read only open */
		bwa_context_throw_error(pCtx,BWA_CTX_NOTICE,"Invalid open mode,BWA is assuming a Read-Only open");
		iFlag = BWA_IO_OPEN_RDONLY;
	}
	while( zMode < zEnd ){
		c = zMode[0];
		if( c == 'b' || c == 'B' ){
			iFlag &= ~BWA_IO_OPEN_TEXT;
			iFlag |= BWA_IO_OPEN_BINARY;
		}else if( c == 't' || c == 'T' ){
			iFlag &= ~BWA_IO_OPEN_BINARY;
			iFlag |= BWA_IO_OPEN_TEXT;
		}
		zMode++;
	}
	return iFlag;
}
/*
 * Initialize the IO private structure.
 */
static void InitIOPrivate(bwa_vm *pVm,const bwa_io_stream *pStream,io_private *pOut)
{
	pOut->pStream = pStream;
	SyBlobInit(&pOut->sBuffer,&pVm->sAllocator);
	pOut->nOfft = 0;
	/* Set the magic number */
	pOut->iMagic = IO_PRIVATE_MAGIC;
}
/*
 * Release the IO private structure.
 */
static void ReleaseIOPrivate(bwa_context *pCtx,io_private *pDev)
{
	SyBlobRelease(&pDev->sBuffer);
	pDev->iMagic = 0x2126; /* Invalid magic number so we can detetct misuse */
	/* Release the whole structure */
	bwa_context_free_chunk(pCtx,pDev);
}
/*
 * Reset the IO private structure.
 */
static void ResetIOPrivate(io_private *pDev)
{
	SyBlobReset(&pDev->sBuffer);
	pDev->nOfft = 0;
}
/* Forward declaration */
static int is_php_stream(const bwa_io_stream *pStream);
/*
 * resource fopen(string $filename,string $mode [,bool $use_include_path = false[,resource $context ]])
 *  Open a file,a URL or any other IO stream.
 * Parameters
 *  $filename
 *   If filename is of the form "scheme://...", it is assumed to be a URL and PHP will search
 *   for a protocol handler (also known as a wrapper) for that scheme. If no scheme is given
 *   then a regular file is assumed.
 *  $mode
 *   The mode parameter specifies the type of access you require to the stream
 *   See the block comment associated with the StrModeToFlags() for the supported 
 *   modes.
 *  $use_include_path
 *   You can use the optional second parameter and set it to
 *   TRUE, if you want to search for the file in the include_path, too.
 *  $context
 *   A context stream resource.
 * Return
 *  File handle on success or FALSE on failure.
 */
static int BWA_builtin_fopen(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const bwa_io_stream *pStream;
	const char *zUri,*zMode;
	bwa_value *pResource;
	io_private *pDev;
	int iLen,imLen;
	int iOpenFlags;
	if( nArg < 1 || !bwa_value_is_string(apArg[0]) ){
		/* Missing/Invalid arguments,return FALSE */
		bwa_context_throw_error(pCtx,BWA_CTX_WARNING,"Expecting a file path or URL");
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the URI and the desired access mode */
	zUri  = bwa_value_to_string(apArg[0],&iLen);
	if( nArg > 1 ){
		zMode = bwa_value_to_string(apArg[1],&imLen);
	}else{
		/* Set a default read-only mode */
		zMode = "r";
		imLen = (int)sizeof(char);
	}
	/* Try to extract a stream */
	pStream = BWA_VmGetStreamDevice(pCtx->pVm,&zUri,iLen);
	if( pStream == 0 ){
		bwa_context_throw_error_format(pCtx,BWA_CTX_WARNING,
			"No stream device is associated with the given URI(%s)",zUri);
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Allocate a new IO private instance */
	pDev = (io_private *)bwa_context_alloc_chunk(pCtx,sizeof(io_private),TRUE,FALSE);
	if( pDev == 0 ){
		bwa_context_throw_error(pCtx,BWA_CTX_ERR,"BWA is running out of memory");
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	pResource = 0;
	if( nArg > 3 ){
		pResource = apArg[3];
	}else if( is_php_stream(pStream) ){
		/* TICKET 1433-80: The php:// stream need a bwa_value to access the underlying
		 * virtual machine.
		 */
		pResource = apArg[0];
	}
	/* Initialize the structure */
	InitIOPrivate(pCtx->pVm,pStream,pDev);
	/* Convert open mode to BWA flags */
	iOpenFlags = StrModeToFlags(pCtx,zMode,imLen);
	/* Try to get a handle */
	pDev->pHandle = BWA_StreamOpenHandle(pCtx->pVm,pStream,zUri,iOpenFlags,
		nArg > 2 ? bwa_value_to_bool(apArg[2]) : FALSE,pResource,FALSE,0);
	if( pDev->pHandle == 0 ){
		bwa_context_throw_error_format(pCtx,BWA_CTX_ERR,"IO error while opening '%s'",zUri);
		bwa_result_bool(pCtx,0);
		bwa_context_free_chunk(pCtx,pDev);
		return BWA_OK;
	}
	/* All done,return the io_private instance as a resource */
	bwa_result_resource(pCtx,pDev);
	return BWA_OK;
}
/*
 * bool fclose(resource $handle)
 *  Closes an open file pointer
 * Parameters
 *  $handle
 *   The file pointer. 
 * Return
 *  TRUE on success or FALSE on failure.
 */
static int BWA_builtin_fclose(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const bwa_io_stream *pStream;
	io_private *pDev;
	bwa_vm *pVm;
	if( nArg < 1 || !bwa_value_is_resource(apArg[0]) ){
		/* Missing/Invalid arguments,return FALSE */
		bwa_context_throw_error(pCtx,BWA_CTX_WARNING,"Expecting an IO handle");
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract our private data */
	pDev = (io_private *)bwa_value_to_resource(apArg[0]);
	/* Make sure we are dealing with a valid io_private instance */
	if( IO_PRIVATE_INVALID(pDev) ){
		/*Expecting an IO handle */
		bwa_context_throw_error(pCtx,BWA_CTX_WARNING,"Expecting an IO handle");
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the target IO stream device */
	pStream = pDev->pStream;
	if( pStream == 0 ){
		bwa_context_throw_error_format(pCtx,BWA_CTX_WARNING,
			"IO routine(%s) not implemented in the underlying stream(%s) device,BWA is returning FALSE",
			bwa_function_name(pCtx),pStream ? pStream->zName : "null_stream"
			);
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the VM that own this context */
	pVm = pCtx->pVm;
	/* TICKET 1433-62: Keep the STDIN/STDOUT/STDERR handles open */
	if( pDev != pVm->pStdin && pDev != pVm->pStdout && pDev != pVm->pStderr ){
		/* Perform the requested operation */
		BWA_StreamCloseHandle(pStream,pDev->pHandle);
		/* Release the IO private structure */
		ReleaseIOPrivate(pCtx,pDev);
		/* Invalidate the resource handle */
		bwa_value_release(apArg[0]);
	}
	/* Return TRUE */
	bwa_result_bool(pCtx,1);
	return BWA_OK;
}
