/* int (*xStat)(const char *,bwa_value *,bwa_value *) */
/* int (*xlStat)(const char *,bwa_value *,bwa_value *) */
static int WinVfs_Stat(const char *zPath,bwa_value *pArray,bwa_value *pWorker)
{
	BY_HANDLE_FILE_INFORMATION sInfo;
	void *pConverted;
	HANDLE pHandle;
	BOOL rc;
	pConverted = convertUtf8Filename(zPath);
	if( pConverted == 0 ){
		return -1;
	}
	/* Open the file in read-only mode */
	pHandle = OpenReadOnly((LPCWSTR)pConverted);
	HeapFree(GetProcessHeap(),0,pConverted);
	if( pHandle == 0 ){
		return -1;
	}
	rc = GetFileInformationByHandle(pHandle,&sInfo);
	CloseHandle(pHandle);
	if( !rc ){
		return -1;
	}
	/* dev */
	bwa_value_int64(pWorker,(bwa_int64)sInfo.dwVolumeSerialNumber);
	bwa_array_add_strkey_elem(pArray,"dev",pWorker); /* Will make it's own copy */
	/* ino */
	bwa_value_int64(pWorker,(bwa_int64)(((bwa_int64)sInfo.nFileIndexHigh << 32) | sInfo.nFileIndexLow));
	bwa_array_add_strkey_elem(pArray,"ino",pWorker); /* Will make it's own copy */
	/* mode */
	bwa_value_int(pWorker,0);
	bwa_array_add_strkey_elem(pArray,"mode",pWorker);
	/* nlink */
	bwa_value_int(pWorker,(int)sInfo.nNumberOfLinks);
	bwa_array_add_strkey_elem(pArray,"nlink",pWorker); /* Will make it's own copy */
	/* uid,gid,rdev */
	bwa_value_int(pWorker,0);
	bwa_array_add_strkey_elem(pArray,"uid",pWorker);
	bwa_array_add_strkey_elem(pArray,"gid",pWorker);
	bwa_array_add_strkey_elem(pArray,"rdev",pWorker);
	/* size */
	bwa_value_int64(pWorker,(bwa_int64)(((bwa_int64)sInfo.nFileSizeHigh << 32) | sInfo.nFileSizeLow));
	bwa_array_add_strkey_elem(pArray,"size",pWorker); /* Will make it's own copy */
	/* atime */
	bwa_value_int64(pWorker,convertWindowsTimeToUnixTime(&sInfo.ftLastAccessTime));
	bwa_array_add_strkey_elem(pArray,"atime",pWorker); /* Will make it's own copy */
	/* mtime */
	bwa_value_int64(pWorker,convertWindowsTimeToUnixTime(&sInfo.ftLastWriteTime));
	bwa_array_add_strkey_elem(pArray,"mtime",pWorker); /* Will make it's own copy */
	/* ctime */
	bwa_value_int64(pWorker,convertWindowsTimeToUnixTime(&sInfo.ftCreationTime));
	bwa_array_add_strkey_elem(pArray,"ctime",pWorker); /* Will make it's own copy */
	/* blksize,blocks */
	bwa_value_int(pWorker,0);		
	bwa_array_add_strkey_elem(pArray,"blksize",pWorker);
	bwa_array_add_strkey_elem(pArray,"blocks",pWorker);
	return BWA_OK;
}
