/* int (*xFiletype)(const char *,bwa_context *) */
static int WinVfs_Filetype(const char *zPath,bwa_context *pCtx)
{
	void * pConverted;
	DWORD dwAttr;
	pConverted = convertUtf8Filename(zPath);
	if( pConverted == 0 ){
		/* Expand 'unknown' */
		bwa_result_string(pCtx,"unknown",sizeof("unknown")-1);
		return -1;
	}
	dwAttr = GetFileAttributesW((LPCWSTR)pConverted);
	HeapFree(GetProcessHeap(),0,pConverted);
	if( dwAttr == INVALID_FILE_ATTRIBUTES ){
		/* Expand 'unknown' */
		bwa_result_string(pCtx,"unknown",sizeof("unknown")-1);
		return -1;
	}
	if(dwAttr & (FILE_ATTRIBUTE_HIDDEN|FILE_ATTRIBUTE_NORMAL|FILE_ATTRIBUTE_ARCHIVE) ){
		bwa_result_string(pCtx,"file",sizeof("file")-1);
	}else if(dwAttr & FILE_ATTRIBUTE_DIRECTORY){
		bwa_result_string(pCtx,"dir",sizeof("dir")-1);
	}else if(dwAttr & FILE_ATTRIBUTE_REPARSE_POINT){
		bwa_result_string(pCtx,"link",sizeof("link")-1);
	}else if(dwAttr & (FILE_ATTRIBUTE_DEVICE)){
		bwa_result_string(pCtx,"block",sizeof("block")-1);
	}else{
		bwa_result_string(pCtx,"unknown",sizeof("unknown")-1);
	}
	return BWA_OK;
}
/* int (*xGetenv)(const char *,bwa_context *) */
static int WinVfs_Getenv(const char *zVar,bwa_context *pCtx)
{
	char zValue[1024];
	DWORD n;
	/*
	 * According to MSDN
	 * If lpBuffer is not large enough to hold the data, the return 
	 * value is the buffer size, in characters, required to hold the 
	 * string and its terminating null character and the contents 
	 * of lpBuffer are undefined.
	 */
	n = sizeof(zValue);
	SyMemcpy("Undefined",zValue,sizeof("Undefined")-1);
	/* Extract the environment value */
	n = GetEnvironmentVariableA(zVar,zValue,sizeof(zValue));
	if( !n ){
		/* No such variable*/
		return -1;
	}
	bwa_result_string(pCtx,zValue,(int)n);
	return BWA_OK;
}
