/* int (*xMmap)(const char *,void **,bwa_int64 *) */
static int UnixVfs_Mmap(const char *zPath,void **ppMap,bwa_int64 *pSize)
{
	struct stat st;
	void *pMap;
	int fd;
	int rc;
	/* Open the file in a read-only mode */
	fd = open(zPath,O_RDONLY);
	if( fd < 0 ){
		return -1;
	}
	/* stat the handle */
	fstat(fd,&st);
	/* Obtain a memory view of the whole file */
	pMap = mmap(0,st.st_size,PROT_READ,MAP_PRIVATE|MAP_FILE,fd,0);
	rc = BWA_OK;
	if( pMap == MAP_FAILED ){
		rc = -1;
	}else{
		/* Point to the memory view */
		*ppMap = pMap;
		*pSize = (bwa_int64)st.st_size;
	}
	close(fd);
	return rc;
}
