/* void (*xTempDir)(bwa_context *) */
static void UnixVfs_TempDir(bwa_context *pCtx)
{
	static const char *azDirs[] = {
     "/var/tmp",
     "/usr/tmp",
	 "/usr/local/tmp"
  };
  unsigned int i;
  struct stat buf;
  const char *zDir;
  zDir = getenv("TMPDIR");
  if( zDir && zDir[0] != 0 && !access(zDir,07) ){
	  bwa_result_string(pCtx,zDir,-1);
	  return;
  }
  for(i=0; i<sizeof(azDirs)/sizeof(azDirs[0]); i++){
	zDir=azDirs[i];
    if( zDir==0 ) continue;
    if( stat(zDir, &buf) ) continue;
    if( !S_ISDIR(buf.st_mode) ) continue;
    if( access(zDir, 07) ) continue;
    /* Got one */
	bwa_result_string(pCtx,zDir,-1);
	return;
  }
  /* Default temp dir */
  bwa_result_string(pCtx,"/tmp",(int)sizeof("/tmp")-1);
}
