/* bwa_int64 (*xRead)(void *,void *,bwa_int64); */
static bwa_int64 UnixFile_Read(void *pUserData,void *pBuffer,bwa_int64 nDatatoRead)
{
	ssize_t nRd;
	nRd = read(SX_PTR_TO_INT(pUserData),pBuffer,(size_t)nDatatoRead);
	if( nRd < 1 ){
		/* EOF or IO error */
		return -1;
	}
	return (bwa_int64)nRd;
}
/* bwa_int64 (*xWrite)(void *,const void *,bwa_int64); */
static bwa_int64 UnixFile_Write(void *pUserData,const void *pBuffer,bwa_int64 nWrite)
{
	const char *zData = (const char *)pBuffer;
	int fd = SX_PTR_TO_INT(pUserData);
	bwa_int64 nCount;
	ssize_t nWr;
	nCount = 0;
	for(;;){
		if( nWrite < 1 ){
			break;
		}
		nWr = write(fd,zData,(size_t)nWrite);
		if( nWr < 1 ){
			/* IO error */
			break;
		}
		nWrite -= nWr;
		nCount += nWr;
		zData += nWr;
	}
	if( nWrite > 0 ){
		return -1;
	}
	return nCount;
}
/* int (*xSeek)(void *,bwa_int64,int) */
static int UnixFile_Seek(void *pUserData,bwa_int64 iOfft,int whence)
{
	off_t iNew;
	switch(whence){
	case 1:/*SEEK_CUR*/
		whence = SEEK_CUR;
		break;
	case 2: /* SEEK_END */
		whence = SEEK_END;
		break;
	case 0: /* SEEK_SET */
	default:
		whence = SEEK_SET;
		break;
	}
	iNew = lseek(SX_PTR_TO_INT(pUserData),(off_t)iOfft,whence);
	if( iNew < 0 ){
		return -1;
	}
	return BWA_OK;
}
/* int (*xLock)(void *,int) */
static int UnixFile_Lock(void *pUserData,int lock_type)
{
	int fd = SX_PTR_TO_INT(pUserData);
	int rc = BWA_OK; /* cc warning */
	if( lock_type < 0 ){
		/* Unlock the file */
		rc = flock(fd,LOCK_UN);
	}else{
		if( lock_type == 1 ){
			/* Exculsive lock */
			rc = flock(fd,LOCK_EX);
		}else{
			/* Shared lock */
			rc = flock(fd,LOCK_SH);
		}
	}
	return !rc ? BWA_OK : -1;
}
/* bwa_int64 (*xTell)(void *) */
static bwa_int64 UnixFile_Tell(void *pUserData)
{
	off_t iNew;
	iNew = lseek(SX_PTR_TO_INT(pUserData),0,SEEK_CUR);
	return (bwa_int64)iNew;
}
