	bwa_array_add_strkey_elem(pArray,"mode",pWorker);
	/* nlink */
	bwa_value_int(pWorker,(int)st.st_nlink);
	bwa_array_add_strkey_elem(pArray,"nlink",pWorker); /* Will make it's own copy */
	/* uid,gid,rdev */
	bwa_value_int(pWorker,(int)st.st_uid);
	bwa_array_add_strkey_elem(pArray,"uid",pWorker);
	bwa_value_int(pWorker,(int)st.st_gid);
	bwa_array_add_strkey_elem(pArray,"gid",pWorker);
	bwa_value_int(pWorker,(int)st.st_rdev);
	bwa_array_add_strkey_elem(pArray,"rdev",pWorker);
	/* size */
	bwa_value_int64(pWorker,(bwa_int64)st.st_size);
	bwa_array_add_strkey_elem(pArray,"size",pWorker); /* Will make it's own copy */
	/* atime */
	bwa_value_int64(pWorker,(bwa_int64)st.st_atime);
	bwa_array_add_strkey_elem(pArray,"atime",pWorker); /* Will make it's own copy */
	/* mtime */
	bwa_value_int64(pWorker,(bwa_int64)st.st_mtime);
	bwa_array_add_strkey_elem(pArray,"mtime",pWorker); /* Will make it's own copy */
	/* ctime */
	bwa_value_int64(pWorker,(bwa_int64)st.st_ctime);
	bwa_array_add_strkey_elem(pArray,"ctime",pWorker); /* Will make it's own copy */
	/* blksize,blocks */
	bwa_value_int(pWorker,(int)st.st_blksize);		
	bwa_array_add_strkey_elem(pArray,"blksize",pWorker);
	bwa_value_int(pWorker,(int)st.st_blocks);
	bwa_array_add_strkey_elem(pArray,"blocks",pWorker);
	return BWA_OK;
