/*
 * The following defines are mostly used by the UNIX built and have
 * no particular meaning on windows.
 */
#ifndef STDIN_FILENO
#define STDIN_FILENO	0
#endif
#ifndef STDOUT_FILENO
#define STDOUT_FILENO	1
#endif
#ifndef STDERR_FILENO
#define STDERR_FILENO	2
#endif
/*
 * php:// Accessing various I/O streams
 * According to the PHP langage reference manual
 * PHP provides a number of miscellaneous I/O streams that allow access to PHP's own input
 * and output streams, the standard input, output and error file descriptors.
 * php://stdin, php://stdout and php://stderr:
 *  Allow direct access to the corresponding input or output stream of the PHP process.
 *  The stream references a duplicate file descriptor, so if you open php://stdin and later
 *  close it, you close only your copy of the descriptor-the actual stream referenced by STDIN is unaffected.
 *  php://stdin is read-only, whereas php://stdout and php://stderr are write-only.
 * php://output
 *  php://output is a write-only stream that allows you to write to the output buffer 
 *  mechanism in the same way as print and echo. 
 */
typedef struct bwa_stream_data bwa_stream_data;
/* Supported IO streams */
#define BWA_IO_STREAM_STDIN  1 /* php://stdin */
#define BWA_IO_STREAM_STDOUT 2 /* php://stdout */
#define BWA_IO_STREAM_STDERR 3 /* php://stderr */
#define BWA_IO_STREAM_OUTPUT 4 /* php://output */
 /* The following structure is the private data associated with the php:// stream */
struct bwa_stream_data
{
	bwa_vm *pVm; /* VM that own this instance */
	int iType;   /* Stream type */
	union{
		void *pHandle; /* Stream handle */
		bwa_output_consumer sConsumer; /* VM output consumer */
	}x;
};
/*
 * Allocate a new instance of the bwa_stream_data structure.
 */
static bwa_stream_data * PHPStreamDataInit(bwa_vm *pVm,int iType)
{
	bwa_stream_data *pData;
	if( pVm == 0 ){
		return 0;
	}
	/* Allocate a new instance */
	pData = (bwa_stream_data *)SyMemBackendAlloc(&pVm->sAllocator,sizeof(bwa_stream_data));
	if( pData == 0 ){
		return 0;
	}
	/* Zero the structure */
	SyZero(pData,sizeof(bwa_stream_data));
	/* Initialize fields */
	pData->iType = iType;
	if( iType == BWA_IO_STREAM_OUTPUT ){
		/* Point to the default VM consumer routine. */
		pData->x.sConsumer = pVm->sVmConsumer; 
	}else{
#ifdef __WINNT__
		DWORD nChannel;
		switch(iType){
		case BWA_IO_STREAM_STDOUT:	nChannel = STD_OUTPUT_HANDLE; break;
		case BWA_IO_STREAM_STDERR:  nChannel = STD_ERROR_HANDLE; break;
		default:
			nChannel = STD_INPUT_HANDLE; 
			break;
		}
		pData->x.pHandle = GetStdHandle(nChannel);
#else
		/* Assume an UNIX system */
		int ifd = STDIN_FILENO;
		switch(iType){
		case BWA_IO_STREAM_STDOUT:  ifd = STDOUT_FILENO; break;
		case BWA_IO_STREAM_STDERR:  ifd = STDERR_FILENO; break;
		default:
			break;
		}
		pData->x.pHandle = SX_INT_TO_PTR(ifd);
#endif
	}
	pData->pVm = pVm;
	return pData;
}
/* int (*xOpen)(const char *,int,bwa_value *,void **) */
static int PHPStreamData_Open(const char *zName,int iMode,bwa_value *pResource,void ** ppHandle)
{
	bwa_stream_data *pData;
	SyString sStream;
	SyStringInitFromBuf(&sStream,zName,SyStrlen(zName));
	/* Trim leading and trailing white spaces */
	SyStringFullTrim(&sStream);
	/* Stream to open */
	if( SyStrnicmp(sStream.zString,"stdin",sizeof("stdin")-1) == 0 ){
		iMode = BWA_IO_STREAM_STDIN;
	}else if( SyStrnicmp(sStream.zString,"output",sizeof("output")-1) == 0 ){
		iMode = BWA_IO_STREAM_OUTPUT;
	}else if( SyStrnicmp(sStream.zString,"stdout",sizeof("stdout")-1) == 0 ){
		iMode = BWA_IO_STREAM_STDOUT;
	}else if( SyStrnicmp(sStream.zString,"stderr",sizeof("stderr")-1) == 0 ){
		iMode = BWA_IO_STREAM_STDERR;
	}else{
		/* unknown stream name */
		return -1;
	}
	/* Create our handle */
	pData = PHPStreamDataInit(pResource?pResource->pVm:0,iMode);
	if( pData == 0 ){
		return -1;
	}
	/* Make the handle public */
	*ppHandle = (void *)pData;
	return BWA_OK;
}
/* bwa_int64 (*xRead)(void *,void *,bwa_int64) */
static bwa_int64 PHPStreamData_Read(void *pHandle,void *pBuffer,bwa_int64 nDatatoRead)
{
	bwa_stream_data *pData = (bwa_stream_data *)pHandle;
	if( pData == 0 ){
		return -1;
	}
	if( pData->iType != BWA_IO_STREAM_STDIN ){
		/* Forbidden */
		return -1;
	}
#ifdef __WINNT__
	{
		DWORD nRd;
		BOOL rc;
		rc = ReadFile(pData->x.pHandle,pBuffer,(DWORD)nDatatoRead,&nRd,0);
		if( !rc ){
			/* IO error */
			return -1;
		}
		return (bwa_int64)nRd;
	}
#elif defined(__UNIXES__)
	{
		ssize_t nRd;
		int fd;
		fd = SX_PTR_TO_INT(pData->x.pHandle);
		nRd = read(fd,pBuffer,(size_t)nDatatoRead);
		if( nRd < 1 ){
			return -1;
		}
		return (bwa_int64)nRd;
	}
#else
	return -1;
#endif
}
/* bwa_int64 (*xWrite)(void *,const void *,bwa_int64) */
static bwa_int64 PHPStreamData_Write(void *pHandle,const void *pBuf,bwa_int64 nWrite)
{
	bwa_stream_data *pData = (bwa_stream_data *)pHandle;
	if( pData == 0 ){
		return -1;
	}
	if( pData->iType == BWA_IO_STREAM_STDIN ){
		/* Forbidden */
		return -1;
	}else if( pData->iType == BWA_IO_STREAM_OUTPUT ){
		bwa_output_consumer *pCons = &pData->x.sConsumer;
		int rc;
		/* Call the vm output consumer */
		rc = pCons->xConsumer(pBuf,(unsigned int)nWrite,pCons->pUserData);
		if( rc == BWA_ABORT ){
			return -1;
		}
		return nWrite;
	}
#ifdef __WINNT__
	{
		DWORD nWr;
		BOOL rc;
		rc = WriteFile(pData->x.pHandle,pBuf,(DWORD)nWrite,&nWr,0);
		if( !rc ){
			/* IO error */
			return -1;
		}
		return (bwa_int64)nWr;
	}
#elif defined(__UNIXES__)
	{
		ssize_t nWr;
		int fd;
		fd = SX_PTR_TO_INT(pData->x.pHandle);
		nWr = write(fd,pBuf,(size_t)nWrite);
		if( nWr < 1 ){
			return -1;
		}
		return (bwa_int64)nWr;
	}
#else
	return -1;
#endif
}
/* void (*xClose)(void *) */
static void PHPStreamData_Close(void *pHandle)
{
	bwa_stream_data *pData = (bwa_stream_data *)pHandle;
	bwa_vm *pVm;
	if( pData == 0 ){
		return;
	}
	pVm = pData->pVm;
	/* Free the instance */
	SyMemBackendFree(&pVm->sAllocator,pData);
}
