/*
 * array array_combine(array $keys,array $values)
 *  Creates an array by using one array for keys and another for its values.
 * Parameters
 *  $keys
 *    Array of keys to be used.
 * $values
 *   Array of values to be used.
 * Return
 *  Returns the combined array. Otherwise FALSE if the number of elements
 *  for each array isn't equal or if one of the given arguments is
 *  not an array. 
 */
static int bwa_hashmap_combine(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pKe,*pVe;
	bwa_hashmap *pKey,*pValue;
	bwa_value *pArray;
	sxu32 n;
	if( nArg < 2 ){
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Make sure we are dealing with a valid hashmap */
	if( !bwa_value_is_array(apArg[0]) || !bwa_value_is_array(apArg[1]) ){
		/* Invalid argument,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmaps */
	pKey   = (bwa_hashmap *)apArg[0]->x.pOther;
	pValue = (bwa_hashmap *)apArg[1]->x.pOther;
	if( pKey->nEntry != pValue->nEntry ){
		/* Array length differs,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Perform the requested operation */
	pKe = pKey->pFirst;
	pVe = pValue->pFirst;
	for( n = 0 ; n < pKey->nEntry ; n++ ){
		bwa_array_add_elem(pArray,HashmapExtractNodeValue(pKe),HashmapExtractNodeValue(pVe));
		/* Point to the next entry */
		pKe = pKe->pPrev; /* Reverse link */
		pVe = pVe->pPrev;
	}
	/* Return the filled array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_reverse(array $array [,bool $preserve_keys = false ])
 *  Return an array with elements in reverse order.
 * Parameters
 *  $array
 *   The input array.
 *  $preserve_keys (optional)
 *   If set to TRUE keys are preserved.
 * Return
 *  The reversed array.
 */
static int bwa_hashmap_reverse(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pSrc;
	bwa_value *pArray;
	int bPreserve;
	sxu32 n;
	if( nArg < 1 ){
		/* Missing arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Make sure we are dealing with a valid hashmap */
	if( !bwa_value_is_array(apArg[0]) ){
		/* Invalid argument,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	bPreserve = FALSE;
	if( nArg > 1 && bwa_value_is_bool(apArg[1]) ){
		bPreserve = bwa_value_to_bool(apArg[1]);
	}
	/* Point to the internal representation of the input hashmap */
	pSrc = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Perform the requested operation */
	pEntry = pSrc->pLast;
	for( n = 0 ; n < pSrc->nEntry ; n++ ){
		HashmapInsertNode((bwa_hashmap *)pArray->x.pOther,pEntry,bPreserve);
		/* Point to the previous entry */
		pEntry = pEntry->pNext; /* Reverse link */
	}
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_unique(array $array[,int $sort_flags = SORT_STRING ])
 *  Removes duplicate values from an array
 * Parameter
 *  $array
 *   The input array.
 *  $sort_flags
 *    The optional second parameter sort_flags may be used to modify the sorting behavior using these values:
 *    Sorting type flags:
 *       SORT_REGULAR - compare items normally (don't change types)
 *       SORT_NUMERIC - compare items numerically
 *       SORT_STRING - compare items as strings
 *       SORT_LOCALE_STRING - compare items as
 * Return 
 *  Filtered array or NULL on failure.
 */
static int bwa_hashmap_unique(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pEntry;
	bwa_value *pNeedle;
	bwa_hashmap *pSrc;
	bwa_value *pArray;
	int bStrict;
	sxi32 rc;
	sxu32 n;
	if( nArg < 1 ){
		/* Missing arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Make sure we are dealing with a valid hashmap */
	if( !bwa_value_is_array(apArg[0]) ){
		/* Invalid argument,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	bStrict = FALSE;
	if( nArg > 1 ){
		bStrict = bwa_value_to_int(apArg[1]) == 3 /* SORT_REGULAR */ ? 1 : 0;
	}
	/* Point to the internal representation of the input hashmap */
	pSrc = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Perform the requested operation */
	pEntry = pSrc->pFirst;
	for( n = 0 ; n < pSrc->nEntry ; n++ ){
		pNeedle = HashmapExtractNodeValue(pEntry);
		rc = SXERR_NOTFOUND;
		if( pNeedle ){
			rc = HashmapFindValue((bwa_hashmap *)pArray->x.pOther,pNeedle,0,bStrict);
		}
		if( rc != SXRET_OK ){
			/* Perform the insertion */
			HashmapInsertNode((bwa_hashmap *)pArray->x.pOther,pEntry,TRUE);
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	/* Return the freshly created array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_flip(array $input)
 *  Exchanges all keys with their associated values in an array.
 * Parameter
 *  $input
 *   Input array.
 * Return
 *   The flipped array on success or NULL on failure.
 */
static int bwa_hashmap_flip(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pSrc;
	bwa_value *pArray;
	bwa_value *pKey;
	bwa_value sVal;
	sxu32 n;
	if( nArg < 1 ){
		/* Missing arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Make sure we are dealing with a valid hashmap */
	if( !bwa_value_is_array(apArg[0]) ){
		/* Invalid argument,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pSrc = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Start processing */
	pEntry = pSrc->pFirst;
	for( n = 0 ; n < pSrc->nEntry ; n++ ){
		/* Extract the node value */
		pKey = HashmapExtractNodeValue(pEntry);
		if( pKey && (pKey->iFlags & MEMOBJ_NULL) == 0){
			/* Prepare the value for insertion */
			if( pEntry->iType == HASHMAP_INT_NODE ){
				BWA_MemObjInitFromInt(pSrc->pVm,&sVal,pEntry->xKey.iKey);
			}else{
				SyString sStr;
				SyStringInitFromBuf(&sStr,SyBlobData(&pEntry->xKey.sKey),SyBlobLength(&pEntry->xKey.sKey));
				BWA_MemObjInitFromString(pSrc->pVm,&sVal,&sStr);
			}
			/* Perform the insertion */
			bwa_array_add_elem(pArray,pKey,&sVal);
			/* Safely release the value because each inserted entry 
			 * have it's own private copy of the value.
			 */
			BWA_MemObjRelease(&sVal);
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	/* Return the freshly created array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * number array_sum(array $array )
 *  Calculate the sum of values in an array.
 * Parameters
 *  $array: The input array.
 * Return
 *  Returns the sum of values as an integer or float.
 */
static void DoubleSum(bwa_context *pCtx,bwa_hashmap *pMap)
{
	bwa_hashmap_node *pEntry;
	bwa_value *pObj;
	double dSum = 0;
	sxu32 n;
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		pObj = HashmapExtractNodeValue(pEntry);
		if( pObj && (pObj->iFlags & (MEMOBJ_NULL|MEMOBJ_HASHMAP|MEMOBJ_OBJ|MEMOBJ_RES)) == 0){
			if( pObj->iFlags & MEMOBJ_REAL ){
				dSum += pObj->rVal;
			}else if( pObj->iFlags & (MEMOBJ_INT|MEMOBJ_BOOL) ){
				dSum += (double)pObj->x.iVal;
			}else if( pObj->iFlags & MEMOBJ_STRING ){
				if( SyBlobLength(&pObj->sBlob) > 0 ){
					double dv = 0;
					SyStrToReal((const char *)SyBlobData(&pObj->sBlob),SyBlobLength(&pObj->sBlob),(void *)&dv,0);
					dSum += dv;
				}
			}
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	/* Return sum */
	bwa_result_double(pCtx,dSum);
}
static void Int64Sum(bwa_context *pCtx,bwa_hashmap *pMap)
{
	bwa_hashmap_node *pEntry;
	bwa_value *pObj;
	sxi64 nSum = 0;
	sxu32 n;
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		pObj = HashmapExtractNodeValue(pEntry);
		if( pObj && (pObj->iFlags & (MEMOBJ_NULL|MEMOBJ_HASHMAP|MEMOBJ_OBJ|MEMOBJ_RES)) == 0){
			if( pObj->iFlags & MEMOBJ_REAL ){
				nSum += (sxi64)pObj->rVal;
			}else if( pObj->iFlags & (MEMOBJ_INT|MEMOBJ_BOOL) ){
				nSum += pObj->x.iVal;
			}else if( pObj->iFlags & MEMOBJ_STRING ){
				if( SyBlobLength(&pObj->sBlob) > 0 ){
					sxi64 nv = 0;
					SyStrToInt64((const char *)SyBlobData(&pObj->sBlob),SyBlobLength(&pObj->sBlob),(void *)&nv,0);
					nSum += nv;
				}
			}
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	/* Return sum */
	bwa_result_int64(pCtx,nSum);
}
/* number array_sum(array $array ) 
 * (See block-coment above)
 */
static int bwa_hashmap_sum(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap *pMap;
	bwa_value *pObj;
	if( nArg < 1 ){
		/* Missing arguments,return 0 */
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	/* Make sure we are dealing with a valid hashmap */
	if( !bwa_value_is_array(apArg[0]) ){
		/* Invalid argument,return 0 */
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	if( pMap->nEntry < 1 ){
		/* Nothing to compute,return 0 */
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	/* If the first element is of type float,then perform floating
	 * point computaion.Otherwise switch to int64 computaion.
	 */
	pObj = HashmapExtractNodeValue(pMap->pFirst);
	if( pObj == 0 ){
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	if( pObj->iFlags & MEMOBJ_REAL ){
		DoubleSum(pCtx,pMap);
	}else{
		Int64Sum(pCtx,pMap);
	}
	return BWA_OK;
}
