/*
 * value array_rand(array $input[,int $num_req = 1 ])
 *  Pick one or more random entries out of an array.
 * Parameters
 * $input
 *  The input array.
 * $num_req
 *  Specifies how many entries you want to pick.
 * Return
 *  If you are picking only one entry, array_rand() returns the key for a random entry.
 *  Otherwise, it returns an array of keys for the random entries.
 *  NULL is returned on failure.
 */
static int bwa_hashmap_rand(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pNode;
	bwa_hashmap *pMap;
	int nItem = 1;
	if( nArg < 1 ){
		/* Missing argument,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Make sure we are dealing with an array */
	if( !bwa_value_is_array(apArg[0]) ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	if(pMap->nEntry < 1 ){
		/* Empty hashmap,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	if( nArg > 1 ){
		nItem = bwa_value_to_int(apArg[1]);
	}
	if( nItem < 2 ){
		sxu32 nEntry;
		/* Select a random number */
		nEntry = BWA_VmRandomNum(pMap->pVm) % pMap->nEntry;
		/* Extract the desired entry.
		 * Note that we perform a linear lookup here (later version must change this)
		 */
		if( nEntry > pMap->nEntry / 2 ){
			pNode = pMap->pLast;
			nEntry = pMap->nEntry - nEntry;
			if( nEntry > 1 ){
				for(;;){
					if( nEntry == 0 ){
						break;
					}
					/* Point to the previous entry */
					pNode = pNode->pNext; /* Reverse link */
					nEntry--;
				}
			}
		}else{
			pNode = pMap->pFirst;
			for(;;){
				if( nEntry == 0 ){
					break;
				}
				/* Point to the next entry */
				pNode = pNode->pPrev; /* Reverse link */
				nEntry--;
			}
		}
		if( pNode->iType == HASHMAP_INT_NODE ){
			/* Int key */
			bwa_result_int64(pCtx,pNode->xKey.iKey);
		}else{
			/* Blob key */
			bwa_result_string(pCtx,(const char *)SyBlobData(&pNode->xKey.sKey),(int)SyBlobLength(&pNode->xKey.sKey));
		}
	}else{
		bwa_value sKey,*pArray;
		bwa_hashmap *pDest;
		/* Create a new array */
		pArray = bwa_context_new_array(pCtx);
		if( pArray == 0 ){
			bwa_result_null(pCtx);
			return BWA_OK;
		}
		/* Point to the internal representation of the hashmap */
		pDest = (bwa_hashmap *)pArray->x.pOther;
		BWA_MemObjInit(pDest->pVm,&sKey);
		/* Copy the first n items */
		pNode = pMap->pFirst;
		if( nItem > (int)pMap->nEntry ){
			nItem = (int)pMap->nEntry;
		}
		while( nItem > 0){
			BWA_HashmapExtractNodeKey(pNode,&sKey);
			BWA_HashmapInsert(pDest,0/* Automatic index assign*/,&sKey);
			BWA_MemObjRelease(&sKey);
			/* Point to the next entry */
			pNode = pNode->pPrev; /* Reverse link */
			nItem--;
		}
		/* Shuffle the array */
		HashmapMergeSort(pDest,HashmapCmpCallback7,0);
		/* Rehash node */
		HashmapSortRehash(pDest);
		/* Return the random array */
		bwa_result_value(pCtx,pArray);
	}
	return BWA_OK;
}
/*
 * array array_chunk (array $input,int $size [,bool $preserve_keys = false ])
 *  Split an array into chunks.
 * Parameters
 * $input
 *   The array to work on
 * $size
 *   The size of each chunk
 * $preserve_keys
 *   When set to TRUE keys will be preserved. Default is FALSE which will reindex
 *   the chunk numerically.
 * Return
 *  Returns a multidimensional numerically indexed array, starting with 
 *  zero, with each dimension containing size elements. 
 */
static int bwa_hashmap_chunk(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_value *pArray,*pChunk;
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pMap;
	int bPreserve;
	sxu32 nChunk;
	sxu32 nSize;
	sxu32 n;
	if( nArg < 2 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Extract the chunk size */
	nSize = (sxu32)bwa_value_to_int(apArg[1]);
	if( nSize < 1 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	if( nSize >= pMap->nEntry ){
		/* Return the whole array */
		bwa_array_add_elem(pArray,0,apArg[0]);
		bwa_result_value(pCtx,pArray);
		return BWA_OK;
	}
	bPreserve = 0;
	if( nArg > 2 ){
		bPreserve = bwa_value_to_bool(apArg[2]);
	}
	/* Start processing */
	pEntry = pMap->pFirst;
	nChunk = 0;
	pChunk = 0;
	n = pMap->nEntry;
	for( ;; ){
		if( n < 1 ){
			if( nChunk > 0 ){
				/* Insert the last chunk */
				bwa_array_add_elem(pArray,0,pChunk); /* Will have it's own copy */
			}
			break;
		}
		if( nChunk < 1 ){
			if( pChunk ){
				/* Put the first chunk */
				bwa_array_add_elem(pArray,0,pChunk); /* Will have it's own copy */
			}
			/* Create a new dimension */
			pChunk = bwa_context_new_array(pCtx); /* Don't worry about freeing memory here,everything
												   * will be automatically released as soon we return
												   * from this function */
			if( pChunk == 0 ){
				break;
			}
			nChunk = nSize;
		}
		/* Insert the entry */
		HashmapInsertNode((bwa_hashmap *)pChunk->x.pOther,pEntry,bPreserve);
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
		nChunk--;
		n--;
	}
	/* Return the multidimensional array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_pad(array $input,int $pad_size,value $pad_value)
 *  Pad array to the specified length with a value.
 * $input
 *   Initial array of values to pad.
 * $pad_size
 *   New size of the array.
 * $pad_value
 *   Value to pad if input is less than pad_size.
 */
static int bwa_hashmap_pad(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap *pMap;
	bwa_value *pArray;
	int nEntry;
	if( nArg < 3 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Extract the total number of desired entry to insert */
	nEntry = bwa_value_to_int(apArg[1]);
	if( nEntry < 0 ){
		nEntry = -nEntry;
		if( nEntry > 1048576 ){
			nEntry = 1048576; /* Limit imposed by PHP */
		}
		if( nEntry > (int)pMap->nEntry ){
			nEntry -= (int)pMap->nEntry;
			/* Insert given items first */
			while( nEntry > 0 ){
				bwa_array_add_elem(pArray,0,apArg[2]);
				nEntry--;
			}
			/* Merge the two arrays */
			HashmapMerge(pMap,(bwa_hashmap *)pArray->x.pOther);
		}else{
			BWA_HashmapDup(pMap,(bwa_hashmap *)pArray->x.pOther);
		}
	}else if( nEntry > 0 ){
		if( nEntry > 1048576 ){
			nEntry = 1048576; /* Limit imposed by PHP */
		}
		if( nEntry > (int)pMap->nEntry ){
			nEntry -= (int)pMap->nEntry;
			/* Merge the two arrays first */
			HashmapMerge(pMap,(bwa_hashmap *)pArray->x.pOther);
			/* Insert given items */
			while( nEntry > 0 ){
				bwa_array_add_elem(pArray,0,apArg[2]);
				nEntry--;
			}
		}else{
			BWA_HashmapDup(pMap,(bwa_hashmap *)pArray->x.pOther);
		}
	}
	/* Return the new array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_replace(array &$array,array &$array1,...)
 *  Replaces elements from passed arrays into the first array.
 * Parameters
 * $array
 *   The array in which elements are replaced.
 * $array1
 *   The array from which elements will be extracted.
 * ....
 *  More arrays from which elements will be extracted.
 *  Values from later arrays overwrite the previous values.
 * Return
 *  Returns an array, or NULL if an error occurs.
 */
static int bwa_hashmap_replace(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap *pMap;
	bwa_value *pArray;
	int i;
	if( nArg < 1 ){
		/* Invalid arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Perform the requested operation */
	for( i = 0 ; i < nArg ; i++ ){
		if( !bwa_value_is_array(apArg[i]) ){
			continue;
		}
		/* Point to the internal representation of the input hashmap */
		pMap = (bwa_hashmap *)apArg[i]->x.pOther;
		HashmapOverwrite(pMap,(bwa_hashmap *)pArray->x.pOther);
	}
	/* Return the new array */
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_filter(array $input [,callback $callback ])
 *  Filters elements of an array using a callback function.
 * Parameters
 *  $input
 *    The array to iterate over
 * $callback
 *    The callback function to use
 *    If no callback is supplied, all entries of input equal to FALSE (see converting to boolean)
 *    will be removed.
 * Return
 *  The filtered array.
 */
static int bwa_hashmap_filter(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pMap;
	bwa_value *pArray;
	bwa_value sResult;   /* Callback result */
	bwa_value *pValue;
	sxi32 rc;
	int keep;
	sxu32 n;
	if( nArg < 1 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	pEntry = pMap->pFirst;
	BWA_MemObjInit(pMap->pVm,&sResult);
	sResult.nIdx = SXU32_HIGH; /* Mark as constant */
	/* Perform the requested operation */
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		/* Extract node value */
		pValue = HashmapExtractNodeValue(pEntry);
		if( nArg > 1 && pValue ){
			/* Invoke the given callback */
			keep = FALSE;
			rc = BWA_VmCallUserFunction(pMap->pVm,apArg[1],1,&pValue,&sResult);
			if( rc == SXRET_OK ){
				/* Perform a boolean cast */
				keep = bwa_value_to_bool(&sResult);
			}
			BWA_MemObjRelease(&sResult);
		}else{
			/* No available callback,check for empty item */
			keep = !BWA_MemObjIsEmpty(pValue);
		}
		if( keep ){
			/* Perform the insertion,now the callback returned true */
			HashmapInsertNode((bwa_hashmap *)pArray->x.pOther,pEntry,TRUE);
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * array array_map(callback $callback,array $arr1)
 *  Applies the callback to the elements of the given arrays.
 * Parameters
 *  $callback
 *   Callback function to run for each element in each array.
 * $arr1
 *   An array to run through the callback function.
 * Return
 *  Returns an array containing all the elements of arr1 after applying
 *  the callback function to each one. 
 * NOTE:
 *  array_map() passes only a single value to the callback. 
 */
static int bwa_hashmap_map(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_value *pArray,*pValue,sKey,sResult;
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pMap;
	sxu32 n;
	if( nArg < 2 || !bwa_value_is_array(apArg[1]) ){
		/* Invalid arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Create a new array */
	pArray = bwa_context_new_array(pCtx);
	if( pArray == 0 ){
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[1]->x.pOther;
	BWA_MemObjInit(pMap->pVm,&sResult);
	BWA_MemObjInit(pMap->pVm,&sKey);
	sResult.nIdx = SXU32_HIGH; /* Mark as constant */
	sKey.nIdx    = SXU32_HIGH; /* Mark as constant */
	/* Perform the requested operation */
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		/* Extrcat the node value */
		pValue = HashmapExtractNodeValue(pEntry);
		if( pValue ){
			sxi32 rc;
			/* Invoke the supplied callback */
			rc = BWA_VmCallUserFunction(pMap->pVm,apArg[0],1,&pValue,&sResult);
			/* Extract the node key */
			BWA_HashmapExtractNodeKey(pEntry,&sKey);
			if( rc != SXRET_OK ){
				/* An error occured while invoking the supplied callback [i.e: not defined] */
				bwa_array_add_elem(pArray,&sKey,pValue); /* Keep the same value */
			}else{
				/* Insert the callback return value */
				bwa_array_add_elem(pArray,&sKey,&sResult);
			}
			BWA_MemObjRelease(&sKey);
			BWA_MemObjRelease(&sResult);
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	bwa_result_value(pCtx,pArray);
	return BWA_OK;
}
/*
 * value array_reduce(array $input,callback $function[, value $initial = NULL ])
 *  Iteratively reduce the array to a single value using a callback function.
 * Parameters
 *  $input
 *   The input array.
 *  $function
 *  The callback function.
 * $initial
 *  If the optional initial is available, it will be used at the beginning 
 *  of the process, or as a final result in case the array is empty.
 * Return
 *  Returns the resulting value. 
 *  If the array is empty and initial is not passed, array_reduce() returns NULL. 
 */
static int bwa_hashmap_reduce(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pMap;
	bwa_value *pValue;
	bwa_value sResult;
	sxu32 n;
	if( nArg < 2 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid/Missing arguments,return NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Assume a NULL initial value */
	BWA_MemObjInit(pMap->pVm,&sResult);
	sResult.nIdx = SXU32_HIGH; /* Mark as constant */
	if( nArg > 2 ){
		/* Set the initial value */
		BWA_MemObjLoad(apArg[2],&sResult);
	}
	/* Perform the requested operation */
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		/* Extract the node value */
		pValue = HashmapExtractNodeValue(pEntry);
		/* Invoke the supplied callback */
		BWA_VmCallUserFunctionAp(pMap->pVm,apArg[1],&sResult,&sResult,pValue,0);
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	bwa_result_value(pCtx,&sResult); /* Will make it's own copy */
	BWA_MemObjRelease(&sResult);
	return BWA_OK;
}
/*
 * bool array_walk(array &$array,callback $funcname [, value $userdata ] )
 *  Apply a user function to every member of an array.
 * Parameters
 *  $array
 *   The input array.
 * $funcname
 *  Typically, funcname takes on two parameters.The array parameter's value being
 *  the first, and the key/index second.
 * Note:
 *  If funcname needs to be working with the actual values of the array,specify the first
 *  parameter of funcname as a reference. Then, any changes made to those elements will 
 *  be made in the original array itself.
 * $userdata
 *  If the optional userdata parameter is supplied, it will be passed as the third parameter
 *  to the callback funcname.
 * Return
 *  Returns TRUE on success or FALSE on failure.
 */
static int bwa_hashmap_walk(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_value *pValue,*pUserData,sKey;
	bwa_hashmap_node *pEntry;
	bwa_hashmap *pMap;
	sxi32 rc;
	sxu32 n;
	if( nArg < 2 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid/Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	pUserData = nArg > 2 ? apArg[2] : 0;
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	BWA_MemObjInit(pMap->pVm,&sKey);
	sKey.nIdx = SXU32_HIGH; /* Mark as constant */
	/* Perform the desired operation */
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		/* Extract the node value */
		pValue = HashmapExtractNodeValue(pEntry);
		if( pValue ){
			/* Extract the entry key */
			BWA_HashmapExtractNodeKey(pEntry,&sKey);
			/* Invoke the supplied callback */
			rc = BWA_VmCallUserFunctionAp(pMap->pVm,apArg[1],0,pValue,&sKey,pUserData,0);
			BWA_MemObjRelease(&sKey);
			if( rc != SXRET_OK ){
				/* An error occured while invoking the supplied callback [i.e: not defined] */
				bwa_result_bool(pCtx,0); /* return FALSE */
				return BWA_OK;
			}
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	/* All done,return TRUE */
	bwa_result_bool(pCtx,1);
	return BWA_OK;
}
/*
 * Apply a user function to every member of an array.(Recurse on array's). 
 * Refer to the [array_walk_recursive()] implementation for more information.
 */
static int HashmapWalkRecursive(
	bwa_hashmap *pMap,    /* Target hashmap */
	bwa_value *pCallback, /* User callback */
	bwa_value *pUserData, /* Callback private data */
	int iNest             /* Nesting level */
	)
{
	bwa_hashmap_node *pEntry;
	bwa_value *pValue,sKey;
	sxi32 rc;
	sxu32 n;
	/* Iterate throw hashmap entries */
	BWA_MemObjInit(pMap->pVm,&sKey);
	sKey.nIdx = SXU32_HIGH; /* Mark as constant */
	pEntry = pMap->pFirst;
	for( n = 0 ; n < pMap->nEntry ; n++ ){
		/* Extract the node value */
		pValue = HashmapExtractNodeValue(pEntry);
		if( pValue ){
			if( pValue->iFlags & MEMOBJ_HASHMAP ){
				if( iNest < 32 ){
					/* Recurse */
					iNest++;
					HashmapWalkRecursive((bwa_hashmap *)pValue->x.pOther,pCallback,pUserData,iNest);
					iNest--;
				}
			}else{
				/* Extract the node key */
				BWA_HashmapExtractNodeKey(pEntry,&sKey);
				/* Invoke the supplied callback */
				rc = BWA_VmCallUserFunctionAp(pMap->pVm,pCallback,0,pValue,&sKey,pUserData,0);
				BWA_MemObjRelease(&sKey);
				if( rc != SXRET_OK ){
					return rc;
				}
			}
		}
		/* Point to the next entry */
		pEntry = pEntry->pPrev; /* Reverse link */
	}
	return SXRET_OK;
}
/*
 * bool array_walk_recursive(array &$array,callback $funcname [, value $userdata ] )
 *  Apply a user function recursively to every member of an array.
 * Parameters
 *  $array
 *   The input array.
 * $funcname
 *  Typically, funcname takes on two parameters.The array parameter's value being
 *  the first, and the key/index second.
 * Note:
 *  If funcname needs to be working with the actual values of the array,specify the first
 *  parameter of funcname as a reference. Then, any changes made to those elements will 
 *  be made in the original array itself.
 * $userdata
 *  If the optional userdata parameter is supplied, it will be passed as the third parameter
 *  to the callback funcname.
 * Return
 *  Returns TRUE on success or FALSE on failure.
 */
static int bwa_hashmap_walk_recursive(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	bwa_hashmap *pMap;
	sxi32 rc;
	if( nArg < 2 || !bwa_value_is_array(apArg[0]) ){
		/* Invalid/Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the internal representation of the input hashmap */
	pMap = (bwa_hashmap *)apArg[0]->x.pOther;
	/* Perform the desired operation */
	rc = HashmapWalkRecursive(pMap,apArg[1],nArg > 2 ? apArg[2] : 0,0);
	/* All done */
	bwa_result_bool(pCtx,rc == SXRET_OK);
	return BWA_OK;
}
