/*
 * static
 *  Expand the name of the current class. 'static' otherwise.
 */
static void BWA_static_Const(bwa_value *pVal,void *pUserData)
{
	bwa_vm *pVm = (bwa_vm *)pUserData;
	bwa_class *pClass;
	/* Extract the target class if available */
	pClass = BWA_VmPeekTopClass(pVm);
	if( pClass ){
		SyString *pName = &pClass->sName;
		/* Expand class name */
		bwa_value_string(pVal,pName->zString,(int)pName->nByte);
	}else{
		/* Expand 'static' */
		bwa_value_string(pVal,"static",sizeof("static")-1);
	}
}
/*
 * self
 * __CLASS__
 *  Expand the name of the current class. NULL otherwise.
 */
static void BWA_self_Const(bwa_value *pVal,void *pUserData)
{
	bwa_vm *pVm = (bwa_vm *)pUserData;
	bwa_class *pClass;
	/* Extract the target class if available */
	pClass = BWA_VmPeekTopClass(pVm);
	if( pClass ){
		SyString *pName = &pClass->sName;
		/* Expand class name */
		bwa_value_string(pVal,pName->zString,(int)pName->nByte);
	}else{
		/* Expand null */
		bwa_value_null(pVal);
	}
}
/* parent
 *  Expand the name of the parent class. NULL otherwise.
 */
static void BWA_parent_Const(bwa_value *pVal,void *pUserData)
{
	bwa_vm *pVm = (bwa_vm *)pUserData;
	bwa_class *pClass;
	/* Extract the target class if available */
	pClass = BWA_VmPeekTopClass(pVm);
	if( pClass && pClass->pBase ){
		SyString *pName = &pClass->pBase->sName;
		/* Expand class name */
		bwa_value_string(pVal,pName->zString,(int)pName->nByte);
	}else{
		/* Expand null */
		bwa_value_null(pVal);
	}
}
