/*
 * int64 hexdec(string $hex_string)
 *  Hexadecimal to decimal.
 * Parameters
 *  $hex_string
 *   The hexadecimal string to convert
 * Return
 *  The decimal representation of hex_string 
 */
static int BWA_builtin_hexdec(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zString,*zEnd;
	bwa_int64 iVal;
	int nLen;
	if( nArg < 1 ){
		/* Missing arguments,return -1 */
		bwa_result_int(pCtx,-1);
		return BWA_OK;
	}
	iVal = 0;
	if( bwa_value_is_string(apArg[0]) ){
		/* Extract the given string */
		zString = bwa_value_to_string(apArg[0],&nLen);
		/* Delimit the string */
		zEnd = &zString[nLen];
		/* Ignore non hex-stream */
		while( zString < zEnd ){
			if( (unsigned char)zString[0] >= 0xc0 ){
				/* UTF-8 stream */
				zString++;
				while( zString < zEnd && (((unsigned char)zString[0] & 0xc0) == 0x80) ){
					zString++;
				}
			}else{
				if( SyisHex(zString[0]) ){
					break;
				}
				/* Ignore */
				zString++;
			}
		}
		if( zString < zEnd ){
			/* Cast */
			SyHexStrToInt64(zString,(sxu32)(zEnd-zString),(void *)&iVal,0);
		}
	}else{
		/* Extract as a 64-bit integer */
		iVal = bwa_value_to_int64(apArg[0]);
	}
	/* Return the number */
	bwa_result_int64(pCtx,iVal);
	return BWA_OK;
}
