/*
 * string base_convert(string $number,int $frombase,int $tobase)
 *  Convert a number between arbitrary bases.
 * Parameters
 * $number
 *  The number to convert
 * $frombase
 *  The base number is in
 * $tobase
 *  The base to convert number to
 * Return
 *  Number converted to base tobase 
 */
static int BWA_builtin_base_convert(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	int nLen,iFbase,iTobase;
	const char *zNum;
	bwa_int64 iNum;
	if( nArg < 3 ){
		/* Return the empty string*/
		bwa_result_string(pCtx,"",0);
		return BWA_OK;
	}
	/* Base numbers */
	iFbase  = bwa_value_to_int(apArg[1]);
	iTobase = bwa_value_to_int(apArg[2]);
	if( bwa_value_is_string(apArg[0]) ){
		/* Extract the target number */
		zNum = bwa_value_to_string(apArg[0],&nLen);
		if( nLen < 1 ){
			/* Return the empty string*/
			bwa_result_string(pCtx,"",0);
			return BWA_OK;
		}
		/* Base conversion */
		switch(iFbase){
		case 16:
			/* Hex */
			SyHexStrToInt64(zNum,(sxu32)nLen,(void *)&iNum,0);
			break;
		case 8:
			/* Octal */
			SyOctalStrToInt64(zNum,(sxu32)nLen,(void *)&iNum,0);
			break;
		case 2:
			/* Binary */
			SyBinaryStrToInt64(zNum,(sxu32)nLen,(void *)&iNum,0);
			break;
		default:
			/* Decimal */
			SyStrToInt64(zNum,(sxu32)nLen,(void *)&iNum,0);
			break;
		}
	}else{
		iNum = bwa_value_to_int64(apArg[0]);
	}
	switch(iTobase){
	case 16:
		/* Hex */
		bwa_result_string_format(pCtx,"%qx",iNum); /* Quad hex */
		break;
	case 8:
		/* Octal */
		bwa_result_string_format(pCtx,"%qo",iNum); /* Quad octal */
		break;
	case 2:
		/* Binary */
		bwa_result_string_format(pCtx,"%qB",iNum); /* Quad binary */
		break;
	default:
		/* Decimal */
		bwa_result_string_format(pCtx,"%qd",iNum); /* Quad decimal */
		break;
	}
	return BWA_OK;
}
/*
 * string substr(string $string,int $start[, int $length ])
 *  Return part of a string.
 * Parameters
 *  $string
 *   The input string. Must be one character or longer.
 * $start
 *   If start is non-negative, the returned string will start at the start'th position
 *   in string, counting from zero. For instance, in the string 'abcdef', the character
 *   at position 0 is 'a', the character at position 2 is 'c', and so forth.
 *   If start is negative, the returned string will start at the start'th character
 *   from the end of string.
 *   If string is less than or equal to start characters long, FALSE will be returned.
 * $length
 *   If length is given and is positive, the string returned will contain at most length
 *   characters beginning from start (depending on the length of string).
 *   If length is given and is negative, then that many characters will be omitted from
 *   the end of string (after the start position has been calculated when a start is negative).
 *   If start denotes the position of this truncation or beyond, false will be returned.
 *   If length is given and is 0, FALSE or NULL an empty string will be returned.
 *   If length is omitted, the substring starting from start until the end of the string
 *   will be returned. 
 * Return
 *  Returns the extracted part of string, or FALSE on failure or an empty string.
 */
static int BWA_builtin_substr(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zSource,*zOfft;
	int nOfft,nLen,nSrcLen;	
	if( nArg < 2 ){
		/* return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the target string */
	zSource = bwa_value_to_string(apArg[0],&nSrcLen);
	if( nSrcLen < 1 ){
		/* Empty string,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	nLen = nSrcLen; /* cc warning */
	/* Extract the offset */
	nOfft = bwa_value_to_int(apArg[1]);
	if( nOfft < 0 ){
		zOfft = &zSource[nSrcLen+nOfft]; 
		if( zOfft < zSource ){
			/* Invalid offset */
			bwa_result_bool(pCtx,0);
			return BWA_OK;
		}
		nLen = (int)(&zSource[nSrcLen]-zOfft);
		nOfft = (int)(zOfft-zSource);
	}else if( nOfft >= nSrcLen ){
		/* Invalid offset */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}else{
		zOfft = &zSource[nOfft];
		nLen = nSrcLen - nOfft;
	}
	if( nArg > 2 ){
		/* Extract the length */
		nLen = bwa_value_to_int(apArg[2]);
		if( nLen == 0 ){
			/* Invalid length,return an empty string */
			bwa_result_string(pCtx,"",0);
			return BWA_OK;
		}else if( nLen < 0 ){
			nLen = nSrcLen + nLen - nOfft;
			if( nLen < 1 ){
				/* Invalid  length */
				nLen = nSrcLen - nOfft;
			}
		}
		if( nLen + nOfft > nSrcLen ){
			/* Invalid length */
			nLen = nSrcLen - nOfft;
		}
	}
	/* Return the substring */
	bwa_result_string(pCtx,zOfft,nLen);
	return BWA_OK;
}
/*
 * int substr_compare(string $main_str,string $str ,int $offset[,int $length[,bool $case_insensitivity = false ]])
 *  Binary safe comparison of two strings from an offset, up to length characters.
 * Parameters
 *  $main_str
 *  The main string being compared.
 *  $str
 *   The secondary string being compared.
 * $offset
 *  The start position for the comparison. If negative, it starts counting from
 *  the end of the string.
 * $length
 *  The length of the comparison. The default value is the largest of the length 
 *  of the str compared to the length of main_str less the offset.
 * $case_insensitivity
 *  If case_insensitivity is TRUE, comparison is case insensitive.
 * Return
 *  Returns < 0 if main_str from position offset is less than str, > 0 if it is greater than
 *  str, and 0 if they are equal. If offset is equal to or greater than the length of main_str
 *  or length is set and is less than 1, substr_compare() prints a warning and returns FALSE. 
 */
static int BWA_builtin_substr_compare(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zSource,*zOfft,*zSub;
	int nOfft,nLen,nSrcLen,nSublen;
	int iCase = 0;
	int rc;
	if( nArg < 3 ){
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the target string */
	zSource = bwa_value_to_string(apArg[0],&nSrcLen);
	if( nSrcLen < 1 ){
		/* Empty string,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	nLen = nSrcLen; /* cc warning */
	/* Extract the substring */
	zSub = bwa_value_to_string(apArg[1],&nSublen);
	if( nSublen < 1 || nSublen > nSrcLen){
		/* Empty string,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the offset */
	nOfft = bwa_value_to_int(apArg[2]);
	if( nOfft < 0 ){
		zOfft = &zSource[nSrcLen+nOfft]; 
		if( zOfft < zSource ){
			/* Invalid offset */
			bwa_result_bool(pCtx,0);
			return BWA_OK;
		}
		nLen = (int)(&zSource[nSrcLen]-zOfft);
		nOfft = (int)(zOfft-zSource);
	}else if( nOfft >= nSrcLen ){
		/* Invalid offset */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}else{
		zOfft = &zSource[nOfft];
		nLen = nSrcLen - nOfft;
	}
	if( nArg > 3 ){
		/* Extract the length */
		nLen = bwa_value_to_int(apArg[3]);
		if( nLen < 1 ){
			/* Invalid  length */
			bwa_result_int(pCtx,1);
			return BWA_OK;
		}else if( nLen + nOfft > nSrcLen ){
			/* Invalid length */
			nLen = nSrcLen - nOfft;
		}
		if( nArg > 4 ){
			/* Case-sensitive or not */
			iCase = bwa_value_to_bool(apArg[4]);
		}
	}
	/* Perform the comparison */
	if( iCase ){
		rc = SyStrnicmp(zOfft,zSub,(sxu32)nLen);
	}else{
		rc = SyStrncmp(zOfft,zSub,(sxu32)nLen);
	}
	/* Comparison result */
	bwa_result_int(pCtx,rc);
	return BWA_OK;
}
/*
 * int substr_count(string $haystack,string $needle[,int $offset = 0 [,int $length ]])
 *  Count the number of substring occurrences.
 * Parameters
 * $haystack
 *   The string to search in
 * $needle
 *   The substring to search for
 * $offset
 *  The offset where to start counting
 * $length (NOT USED)
 *  The maximum length after the specified offset to search for the substring.
 *  It outputs a warning if the offset plus the length is greater than the haystack length.
 * Return
 *  Toral number of substring occurrences.
 */
static int BWA_builtin_substr_count(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zText,*zPattern,*zEnd;
	int nTextlen,nPatlen;
	int iCount = 0;
	sxu32 nOfft;
	sxi32 rc;
	if( nArg < 2 ){
		/* Missing arguments */
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	/* Point to the haystack */
	zText = bwa_value_to_string(apArg[0],&nTextlen);
	/* Point to the neddle */
	zPattern = bwa_value_to_string(apArg[1],&nPatlen);
	if( nTextlen < 1 || nPatlen < 1 || nPatlen > nTextlen ){
		/* NOOP,return zero */
		bwa_result_int(pCtx,0);
		return BWA_OK;
	}
	if( nArg > 2 ){
		int nOfft;
		/* Extract the offset */
		nOfft = bwa_value_to_int(apArg[2]);
		if( nOfft < 0 || nOfft > nTextlen ){
			/* Invalid offset,return zero */
			bwa_result_int(pCtx,0);
			return BWA_OK;
		}
		/* Point to the desired offset */
		zText = &zText[nOfft];
		/* Adjust length */
		nTextlen -= nOfft;
	}
	/* Point to the end of the string */
	zEnd = &zText[nTextlen];
	if( nArg > 3 ){
		int nLen;
		/* Extract the length */
		nLen = bwa_value_to_int(apArg[3]);
		if( nLen < 0 || nLen > nTextlen ){
			/* Invalid length,return 0 */
			bwa_result_int(pCtx,0);
			return BWA_OK;
		}
		/* Adjust pointer */
		nTextlen = nLen;
		zEnd = &zText[nTextlen];
	}
	/* Perform the search */
	for(;;){
		rc = SyBlobSearch((const void *)zText,(sxu32)(zEnd-zText),(const void *)zPattern,nPatlen,&nOfft);
		if( rc != SXRET_OK ){
			/* Pattern not found,break immediately */
			break;
		}
		/* Increment counter and update the offset */
		iCount++;
		zText += nOfft + nPatlen;
		if( zText >= zEnd ){
			break;
		}
	}
	/* Pattern count */
	bwa_result_int(pCtx,iCount);
	return BWA_OK;
}
/*
 * string chunk_split(string $body[,int $chunklen = 76 [, string $end = "\r\n" ]])
 *   Split a string into smaller chunks.
 * Parameters
 *  $body
 *   The string to be chunked.
 * $chunklen
 *   The chunk length.
 * $end
 *   The line ending sequence.
 * Return
 *  The chunked string or NULL on failure.
 */
static int BWA_builtin_chunk_split(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zIn,*zEnd,*zSep = "\r\n";
	int nSepLen,nChunkLen,nLen;
	if( nArg < 1 || !bwa_value_is_string(apArg[0]) ){
		/* Nothing to split,return null */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* initialize/Extract arguments */
	nSepLen = (int)sizeof("\r\n") - 1;
	nChunkLen = 76;
	zIn = bwa_value_to_string(apArg[0],&nLen);
	zEnd = &zIn[nLen];
	if( nArg > 1 ){
		/* Chunk length */
		nChunkLen = bwa_value_to_int(apArg[1]);
		if( nChunkLen < 1 ){
			/* Switch back to the default length */
			nChunkLen = 76;
		}
		if( nArg > 2 ){
			/* Separator */
			zSep = bwa_value_to_string(apArg[2],&nSepLen);
			if( nSepLen < 1 ){
				/* Switch back to the default separator */
				zSep = "\r\n";
				nSepLen = (int)sizeof("\r\n") - 1;
			}
		}
	}
	/* Perform the requested operation */
	if( nChunkLen > nLen ){
		/* Nothing to split,return the string and the separator */
		bwa_result_string_format(pCtx,"%.*s%.*s",nLen,zIn,nSepLen,zSep);
		return BWA_OK;
	}
	while( zIn < zEnd ){
		if( nChunkLen > (int)(zEnd-zIn) ){
			nChunkLen = (int)(zEnd - zIn);
		}
		/* Append the chunk and the separator */
		bwa_result_string_format(pCtx,"%.*s%.*s",nChunkLen,zIn,nSepLen,zSep);
		/* Point beyond the chunk */
		zIn += nChunkLen;
	}
	return BWA_OK;
}
/*
 * string addslashes(string $str)
 *  Quote string with slashes.
 *  Returns a string with backslashes before characters that need
 *  to be quoted in database queries etc. These characters are single
 *  quote ('), double quote ("), backslash (\) and NUL (the NULL byte). 
 * Parameter
 *  str: The string to be escaped.
 * Return
 *  Returns the escaped string
 */
static int BWA_builtin_addslashes(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zCur,*zIn,*zEnd;
	int nLen;
	if( nArg < 1 ){
		/* Nothing to process,retun NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Extract the string to process */
	zIn  = bwa_value_to_string(apArg[0],&nLen);
	if( nLen < 1 ){
		/* Return the empty string */
		bwa_result_string(pCtx,"",0);
		return BWA_OK;
	}
	zEnd = &zIn[nLen];
	zCur = 0; /* cc warning */
	for(;;){
		if( zIn >= zEnd ){
			/* No more input */
			break;
		}
		zCur = zIn;
		while( zIn < zEnd && zIn[0] != '\'' && zIn[0] != '"' && zIn[0] != '\\' ){
			zIn++;
		}
		if( zIn > zCur ){
			/* Append raw contents */
			bwa_result_string(pCtx,zCur,(int)(zIn-zCur));
		}
		if( zIn < zEnd ){
			int c = zIn[0];
			bwa_result_string_format(pCtx,"\\%c",c);
		}
		zIn++;
	}
	return BWA_OK;
}
/*
 * Check if the given character is present in the given mask.
 * Return TRUE if present. FALSE otherwise.
 */
static int cSlashCheckMask(int c,const char *zMask,int nLen)
{
	const char *zEnd = &zMask[nLen];
	while( zMask < zEnd ){
		if( zMask[0] == c ){
			/* Character present,return TRUE */
			return 1;
		}
		/* Advance the pointer */
		zMask++;
	}
	/* Not present */
	return 0;
}
/*
 * string addcslashes(string $str,string $charlist)
 *  Quote string with slashes in a C style.
 * Parameter
 *  $str:
 *    The string to be escaped.
 *  $charlist:
 *    A list of characters to be escaped. If charlist contains characters \n, \r etc.
 *    they are converted in C-like style, while other non-alphanumeric characters 
 *    with ASCII codes lower than 32 and higher than 126 converted to octal representation. 
 * Return
 *  Returns the escaped string.
 * Note:
 *  Range characters [i.e: 'A..Z'] is not implemented in the current release.
 */
static int BWA_builtin_addcslashes(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	const char *zCur,*zIn,*zEnd,*zMask;
	int nLen,nMask;
	if( nArg < 1 ){
		/* Nothing to process,retun NULL */
		bwa_result_null(pCtx);
		return BWA_OK;
	}
	/* Extract the string to process */
	zIn  = bwa_value_to_string(apArg[0],&nLen);
	if( nLen < 1 || nArg < 2 ){
		/* Return the string untouched */
		bwa_result_string(pCtx,zIn,nLen);
		return BWA_OK;
	}
	/* Extract the desired mask */
	zMask = bwa_value_to_string(apArg[1],&nMask);
	zEnd = &zIn[nLen];
	zCur = 0; /* cc warning */
	for(;;){
		if( zIn >= zEnd ){
			/* No more input */
			break;
		}
		zCur = zIn;
		while( zIn < zEnd && !cSlashCheckMask(zIn[0],zMask,nMask) ){
			zIn++;
		}
		if( zIn > zCur ){
			/* Append raw contents */
			bwa_result_string(pCtx,zCur,(int)(zIn-zCur));
		}
		if( zIn < zEnd ){
			int c = zIn[0];
			if( c > 126 || (c < 32 && (!SyisAlphaNum(c)/*EBCDIC*/ && !SyisSpace(c))) ){
				/* Convert to octal */
				bwa_result_string_format(pCtx,"\\%o",c);
			}else{
				bwa_result_string_format(pCtx,"\\%c",c);
			}
		}
		zIn++;
	}
	return BWA_OK;
}
