	bwa_value *pValue;
	sxu32 nOfft;
	sxi32 rc;
	if( nArg < 2 ){
		/* Missing arguments,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the delimiter */
	zDelim = bwa_value_to_string(apArg[0],&nDelim);
	if( nDelim < 1 ){
		/* Empty delimiter,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Extract the string */
	zString = bwa_value_to_string(apArg[1],&nStrlen);
	if( nStrlen < 1 ){
		/* Empty delimiter,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
	/* Point to the end of the string */
	zEnd = &zString[nStrlen];
	/* Create the array */
	pArray =  bwa_context_new_array(pCtx);
	pValue = bwa_context_new_scalar(pCtx);
	if( pArray == 0 || pValue == 0 ){
		/* Out of memory,return FALSE */
		bwa_result_bool(pCtx,0);
		return BWA_OK;
	}
