	iLimit = SXI32_HIGH;
	if( nArg > 2 ){
		iLimit = bwa_value_to_int(apArg[2]);
		 if( iLimit < 0 ){
			iLimit = -iLimit;
		}
		if( iLimit == 0 ){
			iLimit = 1;
		}
		iLimit--;
	}
