static int BWA_builtin_size_format(bwa_context *pCtx,int nArg,bwa_value **apArg)
{
	/*Kilo*/ /*Mega*/ /*Giga*/ /*Tera*/ /*Peta*/ /*Exa*/ /*Zeta*/
	static const char zUnit[] = {"KMGTPEZ"};
	sxi32 nRest,i_32;
	bwa_int64 iSize;
	int c = -1; /* index in zUnit[] */

	if( nArg < 1 ){
		/* Missing argument,return the empty string */
		bwa_result_string(pCtx,"",0);
		return BWA_OK;
	}
	/* Extract the given size */
	iSize = bwa_value_to_int64(apArg[0]);
	if( iSize < 100 /* Bytes */ ){
		/* Don't bother formatting,return immediately */
		bwa_result_string(pCtx,"0.1 KB",(int)sizeof("0.1 KB")-1);
		return BWA_OK;
	}
	for(;;){
		nRest = (sxi32)(iSize & 0x3FF); 
		iSize >>= 10;
		c++;
		if( (iSize & (~0 ^ 1023)) == 0 ){
			break;
		}
	}
	nRest /= 100;
	if( nRest > 9 ){
		nRest = 9;
	}
	if( iSize > 999 ){
		c++;
		nRest = 9;
		iSize = 0;
	}
	i_32 = (sxi32)iSize;
	/* Format */
	bwa_result_string_format(pCtx,"%d.%d %cB",i_32,nRest,zUnit[c]);
	return BWA_OK;
}
