	bwa_value_int(pValue,sTm.tm_min);
	bwa_array_add_strkey_elem(pArray,"minutes",pValue);
	/* Hours */
	bwa_value_int(pValue,sTm.tm_hour);
	bwa_array_add_strkey_elem(pArray,"hours",pValue);
	/* mday */
	bwa_value_int(pValue,sTm.tm_mday);
	bwa_array_add_strkey_elem(pArray,"mday",pValue);
	/* wday */
	bwa_value_int(pValue,sTm.tm_wday);
	bwa_array_add_strkey_elem(pArray,"wday",pValue);
	/* mon */
	bwa_value_int(pValue,sTm.tm_mon+1);
	bwa_array_add_strkey_elem(pArray,"mon",pValue);
	/* year */
	bwa_value_int(pValue,sTm.tm_year);
	bwa_array_add_strkey_elem(pArray,"year",pValue);
	/* yday */
	bwa_value_int(pValue,sTm.tm_yday);
	bwa_array_add_strkey_elem(pArray,"yday",pValue);
	/* Weekday */
	bwa_value_string(pValue,SyTimeGetDay(sTm.tm_wday),-1);
	bwa_array_add_strkey_elem(pArray,"weekday",pValue);
