/*
 * [CAPIREF: bwa_compile_file()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
int bwa_compile_file(bwa *pEngine,const char *zFilePath,bwa_vm **ppOutVm,int iFlags)
{
	const bwa_vfs *pVfs;
	int rc;
	if( ppOutVm ){
		*ppOutVm = 0;
	}
	rc = BWA_OK; /* cc warning */
	if( BWA_ENGINE_MISUSE(pEngine) || SX_EMPTY_STR(zFilePath) ){
		return BWA_CORRUPT;
	}
#if defined(BWA_ENABLE_THREADS)
	 /* Acquire engine mutex */
	 SyMutexEnter(sMPGlobal.pMutexMethods,pEngine->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
	 if( sMPGlobal.nThreadingLevel > BWA_THREAD_LEVEL_SINGLE && 
		 BWA_THRD_ENGINE_RELEASE(pEngine) ){
			 return BWA_ABORT; /* Another thread have released this instance */
	 }
#endif
	 /*
	  * Check if the underlying vfs implement the memory map
	  * [i.e: mmap() under UNIX/MapViewOfFile() under windows] function.
	  */
	 pVfs = pEngine->pVfs;
	 if( pVfs == 0 || pVfs->xMmap == 0 ){
		 /* Memory map routine not implemented */
		 rc = BWA_IO_ERR;
	 }else{
		 void *pMapView = 0; /* cc warning */
		 bwa_int64 nSize = 0; /* cc warning */
		 SyString sScript;
		 /* Try to get a memory view of the whole file */
		 rc = pVfs->xMmap(zFilePath,&pMapView,&nSize);
		 if( rc != BWA_OK ){
			 /* Assume an IO error */
			 rc = BWA_IO_ERR;
		 }else{
			 /* Compile the file */
			 SyStringInitFromBuf(&sScript,pMapView,nSize);
			 rc = ProcessScript(&(*pEngine),ppOutVm,&sScript,iFlags,zFilePath);
			 /* Release the memory view of the whole file */
			 if( pVfs->xUnmap ){
				 pVfs->xUnmap(pMapView,nSize);
			 }
		 }
	 }
#if defined(BWA_ENABLE_THREADS)
	 /* Leave engine mutex */
	 SyMutexLeave(sMPGlobal.pMutexMethods,pEngine->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
#endif
	/* Compilation result */
	return rc;
}
