/*
 * [CAPIREF: bwa_vm_release()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
int bwa_vm_release(bwa_vm *pVm)
{
	bwa *pEngine;
	int rc;
	/* Ticket 1433-002: NULL VM is harmless operation */
	if ( BWA_VM_MISUSE(pVm) ){
		return BWA_CORRUPT;
	}
#if defined(BWA_ENABLE_THREADS)
	 /* Acquire VM mutex */
	 SyMutexEnter(sMPGlobal.pMutexMethods,pVm->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
	 if( sMPGlobal.nThreadingLevel > BWA_THREAD_LEVEL_SINGLE && 
		 BWA_THRD_VM_RELEASE(pVm) ){
			 return BWA_ABORT; /* Another thread have released this instance */
	 }
#endif
	pEngine = pVm->pEngine;
	rc = BWA_VmRelease(&(*pVm));
#if defined(BWA_ENABLE_THREADS)
	 /* Leave VM mutex */
	 SyMutexLeave(sMPGlobal.pMutexMethods,pVm->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
#endif
	if( rc == BWA_OK ){
		/* Unlink from the list of active VM */
#if defined(BWA_ENABLE_THREADS)
			/* Acquire engine mutex */
			SyMutexEnter(sMPGlobal.pMutexMethods,pEngine->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
			if( sMPGlobal.nThreadingLevel > BWA_THREAD_LEVEL_SINGLE && 
				BWA_THRD_ENGINE_RELEASE(pEngine) ){
					return BWA_ABORT; /* Another thread have released this instance */
			}
#endif
		MACRO_LD_REMOVE(pEngine->pVms,pVm);
		pEngine->iVm--;
		/* Release the memory chunk allocated to this VM */
		SyMemBackendPoolFree(&pEngine->sAllocator,pVm);
#if defined(BWA_ENABLE_THREADS)
			/* Leave engine mutex */
			SyMutexLeave(sMPGlobal.pMutexMethods,pEngine->pMutex); /* NO-OP if sMPGlobal.nThreadingLevel != BWA_THREAD_LEVEL_MULTI */
#endif	
	}
	return rc;
}
