/*
 * [CAPIREF: bwa_object_walk()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
int bwa_object_walk(bwa_value *pObject,int (*xWalk)(const char *,bwa_value *,void *),void *pUserData)
{
	int rc;
	if( xWalk == 0 ){
		return BWA_CORRUPT;
	}
	/* Make sure we are dealing with a valid class instance */
	if( (pObject->iFlags & MEMOBJ_OBJ) == 0 ){
		return BWA_CORRUPT;
	}
	/* Start the walk process */
	rc = BWA_ClassInstanceWalk((bwa_class_instance *)pObject->x.pOther,xWalk,pUserData);
	return rc != BWA_OK ? BWA_ABORT /* User callback request an operation abort*/ : BWA_OK;
}
/*
 * [CAPIREF: bwa_object_fetch_attr()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
bwa_value * bwa_object_fetch_attr(bwa_value *pObject,const char *zAttr)
{
	bwa_value *pValue;
	SyString sAttr;
	/* Make sure we are dealing with a valid class instance */
	if( (pObject->iFlags & MEMOBJ_OBJ) == 0 || zAttr == 0 ){
		return 0; 
	}
	SyStringInitFromBuf(&sAttr,zAttr,SyStrlen(zAttr));
	/* Extract the attribute value if available.
	 */
	pValue = BWA_ClassInstanceFetchAttr((bwa_class_instance *)pObject->x.pOther,&sAttr);
	return pValue;
}
/*
 * [CAPIREF: bwa_object_get_class_name()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
const char * bwa_object_get_class_name(bwa_value *pObject,int *pLength)
{
	bwa_class *pClass;
	if( pLength ){
		*pLength = 0;
	}
	/* Make sure we are dealing with a valid class instance */
	if( (pObject->iFlags & MEMOBJ_OBJ) == 0  ){
		return 0; 
	}
	/* Point to the class */
	pClass = ((bwa_class_instance *)pObject->x.pOther)->pClass;
	/* Return the class name */
	if( pLength ){
		*pLength = (int)SyStringLength(&pClass->sName);
	}
	return SyStringData(&pClass->sName);
}
