/*
 * [CAPIREF: bwa_value_string()]
 * Please refer to the official documentation for function purpose and expected parameters.
 */
int bwa_value_string(bwa_value *pVal,const char *zString,int nLen)
{
	if((pVal->iFlags & MEMOBJ_STRING) == 0 ){
		/* Invalidate any prior representation */
		BWA_MemObjRelease(pVal);
		MemObjSetType(pVal,MEMOBJ_STRING);
	}
	if( zString ){
		if( nLen < 0 ){
			/* Compute length automatically */
			nLen = (int)SyStrlen(zString);
		}
		SyBlobAppend(&pVal->sBlob,(const void *)zString,(sxu32)nLen);
	}
	return BWA_OK;
}
